public void updateDesignCurrencyFields()
{
    boolean editCurrencyFields;
    boolean isSpecifiedRate = false;

    if (ledgerConsolidateCurrencyConversion.SourceLegalEntity)
    {
        editCurrencyFields = !element.areAccountingCourrenciesSame();

        if (ledgerConsolidateCurrencyConversion.ConversionDate == LedgerConsolidateConversionDate::Specified)
        {
            isSpecifiedRate = true;
        }
    }

    exchangeRates_ExchangeRateType.allowEdit(editCurrencyFields);
    exchangeRates_ConversionDate.allowEdit(editCurrencyFields);
    exchangeRates_ExchangeRate.allowEdit(editCurrencyFields && isSpecifiedRate);
    exchangeRates_ExchangeRate.mandatory(editCurrencyFields && isSpecifiedRate);

    exchangeRates_ExchangeRateType.mandatory(editCurrencyFields);
}

/// <summary>
/// Validates the <c>LedgerConsolidateSourceDimension</c> records for proper usage of the Segment Order field.
/// </summary>
public void validateSegmentOrder()
{
    LedgerConsolidateSourceDimension    ledgerConsolidateSourceDimension;
    DimensionAttribute                  dimensionAttribute;
    Integer                             ordinalCount = 0;

    while select Specification,Ordinal from ledgerConsolidateSourceDimension join Name from dimensionAttribute
        order by ledgerConsolidateSourceDimension.Ordinal asc
        where ledgerConsolidateSourceDimension.DimensionAttribute == dimensionAttribute.RecId
    {
        if (ledgerConsolidateSourceDimension.Specification == LedgerConsDim::None && ledgerConsolidateSourceDimension.Ordinal != 0)
        {
            // All Dimension Attributes that have a specification of None must have a segment order of 0.
            throw error(strFmt("@SYS335164","@SYS303748","@SYS1369","@SYS8956"));
        }
        else if (ledgerConsolidateSourceDimension.Specification != LedgerConsDim::None && ledgerConsolidateSourceDimension.Ordinal == 0)
        {
            // All Dimension Attributes that have a specification other than None must have a segment order > 0.
            throw error(strFmt("@SYS335165","@SYS303748","@SYS1369","@SYS8956"));
        }
        else if (ledgerConsolidateSourceDimension.Specification != LedgerConsDim::None && ledgerConsolidateSourceDimension.Ordinal != 0)
        {
            // Now we need to validate that (non-zero) segment orders are unique.
            // Does segment order need to be sequential?
            ordinalCount = ordinalCount + 1;
            if (ledgerConsolidateSourceDimension.Ordinal != ordinalCount)
            {
                throw error(strFmt("@SYS335163","@SYS1369","@SYS8956","@SYS303748"));
            }
        }
    }
}

private void validateConsolidationSourceConfiguration()
{
    TmpLedgerConsCompany consolidateSourceCompanies;

    consolidateSourceCompanies.setTmpData(companies);

    select firstonly RecId from consolidateSourceCompanies;

    if (consolidateSourceCompanies.RecId == 0 || (consolidate.transferBudget() == NoYes::No && consolidate.transferCurrent() == NoYes::No))
    {
        throw error("@GeneralLedger:ConsolidateOnlineMissingConfigurations");
    }
}

