public boolean validateWrite()
{
    CompanyInfo companyInfo;
    CurrencyCode    sourceCompanyCurrencyCode, currentCompanyCurrencyCode;

    if (consolidate.isTransferOnly_BR() && consolidate.mode() == LedgerConsMode::Online)
    {
        companyInfo = CompanyInfo::findDataArea(companies.SourceCompany);

        sourceCompanyCurrencyCode = Ledger::accountingCurrency(companyInfo.RecId);
        currentCompanyCurrencyCode = CompanyInfoHelper::standardCurrency();

        if (sourceCompanyCurrencyCode != currentCompanyCurrencyCode)
        {
            warning("@GLS222920");
        }
    }

    return super();
}

