void init()
{
    TmpLedgerConsCompany    tmpCompany;
    container               c = consolidate.companies();
    int                     idx;
    int                     maxIdx = conLen(c);

    while (idx < maxIdx)
    {
        idx += 1;

        [tmpCompany.SourceCompany,
         tmpCompany.Proportion,
         tmpCompany.ProfitLossBalance,
         tmpCompany.FileName] = conPeek(c,idx);

        if (consolidate.mode() == LedgerConsMode::Import || substr(tmpCompany.FileName, 1, 7) == 'http://')
        {
            // On import, force the user to re-upload the file since the temporary file they previously uploaded may not be there.
            tmpCompany.FileName = '';
        }

        tmpCompany.insert();
    }
    companies.setTmpData(tmpCompany);

    super();
}

public boolean validateWrite()
{
    boolean isValid;
    ReasonRefRecID reasonRef = eliminations_ds.object(fieldNum(TmpLedgerConsElimination, ReasonRefRecId)).getValue();
    ReasonCode reasonCode;

    isValid = super();
    reasonCode = ReasonTableRef::find(reasonRef).Reason;
    if (isValid && reasonRef && reasonCode)
    {
        //Check that the reason code is valid for the Ledger account type
        if (!ReasonFormTable::checkReasonCodeForAccountType(reasonRef,LedgerJournalACType::Ledger))
        {
            // The Reason code is not active for account type
            isValid = checkFailed("@SYS115522");
        }
    }

    return isValid;
}

/// <summary>
/// Populates the temporary eliminations tables with the values from the last form record
/// </summary>
void init()
{
    TmpLedgerConsElimination    tmpElimination;
    container               eliminationContainer = consolidate.parmEliminations();
    int                     idx;
    int                     maxIdx = conLen(eliminationContainer);
    LedgerEliminationRule   ledgerEliminationRule;

    while (idx < maxIdx)
    {
        idx++;

        [tmpElimination.RuleId] = conPeek(eliminationContainer,idx);

        ledgerEliminationRule = LedgerEliminationRule::find(tmpElimination.RuleId);
        if (ledgerEliminationRule)
        {
            tmpElimination.Description = ledgerEliminationRule.Description;
            tmpElimination.JournalName = ledgerEliminationRule.JournalName;
            tmpElimination.DateLastRun = ledgerEliminationRule.DateLastRun;
            tmpElimination.insert();
        }
    }

    eliminations.setTmpData(tmpElimination);

    super();
}

