/// <summary>
/// Performs a custom lookup for the budget model so that no submodel records are shown.
/// </summary>
public void lookup()
{
    BudgetModel::lookupBudgetModel(this);
}

public void jumpRef()
{
    ExchangeRateType exchRateType;

    Args args = new Args(element);
    exchRateType = ExchangeRateType::find(consBudgetExchangeRateType.value());
    args.lookupRecord(exchRateType);

    new MenuFunction(menuitemDisplayStr(ExchangeRateType), MenuItemType::Display).run(args);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    // Pass the value of the budget exchange rate type into consolidation process.
    consolidate.parmBudgetExchangeRateType(consBudgetExchangeRateType.value());

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (this.selection() == enum2int(LedgerConsDim::None))
    {
        dimensionsData.Ordinal = 0;
    }

    return ret;
}

public void lookup()
{
    super();

    FileUploadTemporaryStorageResult uploadFileResult = File::GetFileFromUser();
    if (uploadFileResult != null && uploadFileResult.getUploadStatus())
    {
        // FileName contains the full URL of the file readable to the TextIOStream.
        // UserProvidedFileName contains the name of the file provided by the user to indicate they have provided data.
        companies.FileName = uploadFileResult.getDownloadUrl();
        companies.UserProvidedFileName = uploadFileResult.getFileName();
    }
}

/// <summary>
/// Displays a warning if the entered date is in the past.
/// </summary>
/// <returns>
/// true if the entered date is not in the past; otherwise, false.
/// </returns>
public boolean modified()
{
    super();

    if ((str2datetime(this.valueStr(),1) != utcDateTimeNull()) &&
         (DateTimeUtil::addDays(str2datetime(this.valueStr(),1), 1) < DateTimeUtil::utcNow()))
    {
        warning("@SYS114595");
        return false;
    }

    return true;
}

public boolean leave()
{
    boolean                 ret;
    LedgerEliminationRule   ledgerEliminationRule;

    ret = super();

    ledgerEliminationRule = LedgerEliminationRule::find(eliminations.RuleId);

    eliminations.Description = ledgerEliminationRule.Description;
    eliminations.JournalName = ledgerEliminationRule.JournalName;
    eliminations.DateLastRun = ledgerEliminationRule.DateLastRun;

    return ret;
}

/// <summary>
/// Displays a filtered version of the Reasons lookup form to the user based on Ledger <c>AccountType</c>.
/// </summary>
/// <remarks>
/// This method will be called when the user open the reason code lookup form.
/// </remarks>
public void lookup()
{
    ReasonFormTable::reasonCodeLookup(this, LedgerJournalACType::Ledger, LedgerJournalACType::Ledger, true, false);
}

void clicked()
{
    element.validateConsolidationSourceConfiguration();

    element.validateSegmentOrder();
    consolidate.setMayExecute(true);
    super();
}
