private void updateConsolidateAccountFields()
{
    if (UseConsolidateAccount.value() == NoYes::Yes)
    {
        ConsolidateAccountSource.enabled(true);
        ConsolidateAccountSource.visible(true);

        if (ConsolidateAccountSource.selection() == LedgerConsolidateAccountSource::ConsolidationAccountGroup)
        {
            ConsolidateAccountGroup.enabled(true);
            ConsolidateAccountGroup.visible(true);
        }
        else
        {
            ConsolidateAccountGroup.value(0);
            ConsolidateAccountGroup.enabled(false);
            ConsolidateAccountGroup.visible(false);
        }
    }
    else
    {
        ConsolidateAccountSource.selection(0);
        ConsolidateAccountSource.enabled(false);
        ConsolidateAccountSource.visible(false);
        ConsolidateAccountGroup.value(0);
        ConsolidateAccountGroup.enabled(false);
        ConsolidateAccountGroup.visible(false);
        ConsolidateAccountGroup.update();
    }
}

private void updateCurrencyTranslationFields()
{
    boolean editCurrencyFields = false;
    boolean isSpecifiedRate = false;

    if (ledgerConsolidateTemplateCurrency.SourceLegalEntity)
    {
        editCurrencyFields = !element.areSourceAndTargetAccountingCurrenciesEqual();

        if (ledgerConsolidateTemplateCurrency.ConversionDate == LedgerConsolidateConversionDate::Specified)
        {
            isSpecifiedRate = true;
        }
    }

    ExchRateType_ExchangeRateType.allowEdit(editCurrencyFields);
    ExchRateType_ExchangeRateType.mandatory(editCurrencyFields);

    ExchRateType_ConversionDate.allowEdit(editCurrencyFields);

    ExchRateType_ExchangeRate.allowEdit(editCurrencyFields && isSpecifiedRate);
    ExchRateType_ExchangeRate.mandatory(editCurrencyFields && isSpecifiedRate);
}

private void updateBudgetFields()
{
    if (TransferBudget.value() == NoYes::Yes)
    {
        BudgetModels.enabled(true);
        BudgetModels.visible(true);
        BudgetExchangeRateType.enabled(true);
        BudgetExchangeRateType.visible(true);
    }
    else
    {
        BudgetModels.enabled(false);
        BudgetModels.visible(false);
        BudgetExchangeRateType.enabled(false);
        BudgetExchangeRateType.visible(false);
    }
}

private void generateSourceDimensions(RefRecId _currentTemplateRecId)
{
    DimensionAttribute dimensionAttribute;
    LedgerConsolidateTemplateDimension newTemplateDimension;
    LedgerConsolidateTemplateDimension existingTemplateDimension;

    while select Name, RecId from dimensionAttribute
        order by Name asc
        where dimensionAttribute.Type == DimensionAttributeType::CustomList
            || dimensionAttribute.Type == DimensionAttributeType::ExistingList
        notexists join existingTemplateDimension
            where existingTemplateDimension.ConsolidateTemplate == _currentTemplateRecId
                && existingTemplateDimension.DimensionAttribute == dimensionAttribute.RecId
    {
        newTemplateDimension.clear();

        newTemplateDimension.ConsolidateTemplate = _currentTemplateRecId;
        newTemplateDimension.DimensionAttribute = dimensionAttribute.RecId;
        newTemplateDimension.Specification = LedgerConsDim::None;
        newTemplateDimension.Ordinal = 0;
        newTemplateDimension.insert();
    }

    // Removing any records populated from SysLastValue for DimensionAttributes that no longer exist.
    delete_from newTemplateDimension
        notexists join dimensionAttribute
            where dimensionAttribute.RecId == newTemplateDimension.DimensionAttribute;
}

public void init()
{
    if (CompanyInfo::isConsolidationCompany() == NoYes::No)
    {
        throw error("@SYS21028");
    }

    super();
}

