[Hookable(false)]
public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(CompanyInfo), _formControl);

    Query lookupQuery = new Query();
    QueryBuildDataSource companyInfoDataSource = lookupQuery.addDataSource(tableNum(CompanyInfo));
    companyInfoDataSource.addOrderByField(fieldnum(CompanyInfo, DataArea));

    QueryBuildDataSource existingSourceLegalEntities = companyInfoDataSource.addDataSource(tableNum(LedgerConsolidateTemplateLegalEntity));
    existingSourceLegalEntities.joinMode(JoinMode::NoExistsJoin);
    existingSourceLegalEntities.addLink(fieldNum(CompanyInfo, DataArea), fieldNum(LedgerConsolidateTemplateLegalEntity, SourceCompany));
    existingSourceLegalEntities.addRange(fieldNum(LedgerConsolidateTemplateLegalEntity, DataAreaId)).value(curExt());

    sysTableLookup.addLookupfield(fieldNum(CompanyInfo, DataArea));
    sysTableLookup.addLookupfield(fieldNum(CompanyInfo, Name));
    sysTableLookup.parmQuery(lookupQuery);
    sysTableLookup.performFormLookup();
}

