[Hookable(false)]
public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    // It appears that the CompanyInfo is being modified as autoLookup group is currently empty.
    // This script forces the form to display Data Area Id and Name for easier selection of the companies.
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(CompanyInfo), _formReferenceControl, true);

    // Do not call super as we're providing our own custom lookup logic.
    // ret = super(_formReferenceControl);

    // Display the DataArea and Name in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(CompanyInfo, DataArea));
    sysTableLookup.addLookupfield(fieldNum(CompanyInfo, Name));

    Query lookupQuery = new Query();
    QueryBuildDataSource companyInfoDataSource = lookupQuery.addDataSource(tableNum(CompanyInfo));
    companyInfoDataSource.addOrderByField(fieldnum(CompanyInfo, DataArea));

    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    return sysTableLookup.performFormLookup();
}

