        FinancialDimensionGrid_Ordinal.value(0);
    }

    return ret;
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if ((str2datetime(this.valueStr(),1) != utcDateTimeNull()) &&
         (DateTimeUtil::addDays(str2datetime(this.valueStr(),1), 1) < DateTimeUtil::utcNow()))
    {
        warning("@SYS114595");
        ret = false;
    }

    return ret;
}

[Hookable(false)]
public boolean leave()
{
    boolean ret = super();

    LedgerEliminationRule ledgerEliminationRule = LedgerEliminationRule::find(this.valueStr());

    ledgerConsolidateTemplateElimination.Description = ledgerEliminationRule.Description;
    ledgerConsolidateTemplateElimination.JournalName = ledgerEliminationRule.JournalName;
    ledgerConsolidateTemplateElimination.DateLastRun = ledgerEliminationRule.DateLastRun;

    return ret;
}

