private Amount calculatePositiveSum(FieldId _fieldId, TaxReportDataLayout_JP _dataLayout)
{
    Query query = new Query();
    QueryBuildDataSource queryBuildDataSource = query.addDataSource(tableNum(LedgerConsumptionTaxCalcTrans_JP));

    this.updateTaxCalcTransQueryRanges(queryBuildDataSource, _dataLayout);
    queryBuildDataSource.addRange(_fieldId).value(SysQuery::range(0, null, true));

    queryBuildDataSource.addSelectionField(_fieldId, SelectionField::Sum);

    QueryRun queryRun = new QueryRun(query);

    if (!queryRun.next())
    {
        return 0;
    }

    return queryRun.getNo(1).(_fieldId);
}

