public void init()
{
    if (element.args())
    {
        taxReport = element.args().parmObject() as TaxReport_JP;
    }

    if (!taxReport)
    {
        throw error(strfmt("@SYS22456", element.name(), classStr(TaxReport_JP)));
    }

    TaxReportILayoutProperties layoutReportProperties = taxReport.getTaxReportLayout() as TaxReportILayoutProperties;
    if (layoutReportProperties)
    {
        appendix1DataLayout = layoutReportProperties.getAppendix1DataLayout();
        appendix2DataLayout = layoutReportProperties.getAppendix2DataLayout();
        taxReportAddonDataLayout = layoutReportProperties.getTaxReportAddonDataLayout();
        taxReportDataLayout = layoutReportProperties.getTaxReportDataLayout();
    }
    else
    {
        appendix1DataLayout = TaxReportLayout2023_JP::Appendix1DataLayout;
        appendix2DataLayout = TaxReportLayout2023_JP::Appendix2DataLayout;
        taxReportAddonDataLayout = TaxReportLayout2023_JP::TaxReportAddonDataLayout;
        taxReportDataLayout = TaxReportLayout2023_JP::TaxReportDataLayout;
    }


    if (!LedgerConsumptionTaxReportTrans_JP::existByTaxReportParametersVersion(taxReport, TaxRepVersion::Latest, taxReportAddonDataLayout))
    {
        element.calculateReportAddon();
    }

    if (!LedgerConsumptionTaxReportTrans_JP::existByTaxReportParametersVersion(taxReport, TaxRepVersion::Latest, taxReportDataLayout))
    {
        element.calculateTaxReport();
    }

    super();

    element.enableButtonsActive();
    element.parmFileFormat(SRSReportFileFormat::PDF);
    this.updatePrintFileformat();
}

private void calculateReportAddon()
{
    TaxReportCalculateFieldsReportAddon_JP::calculate(
        taxReport.parmTaxPeriod(),
        taxReport.parmFromDate(),
        taxReport.parmToDate(),
        taxReport.parmTypeOfDeclaration(),
        taxReport.parmAmendment(),
        taxReport.parmCalcMethod(),
        taxReportAddonDataLayout,
        appendix1DataLayout);
}

private void calculateTaxReport()
{
    TaxReportCalculateFieldsReport_JP::calculate(
        taxReport.parmTaxPeriod(),
        taxReport.parmFromDate(),
        taxReport.parmToDate(),
        taxReport.parmTypeOfDeclaration(),
        taxReport.parmAmendment(),
        taxReport.parmCalcMethod(),
        taxReportDataLayout,
        appendix1DataLayout,
        appendix2DataLayout);
}

public void init()
{
    super();

    this.query()
        .dataSourceTable(tablenum(LedgerConsumptionTaxReportTrans_JP))
        .addRange(fieldnum(LedgerConsumptionTaxReportTrans_JP, RecId))
        .value(queryValue(LedgerConsumptionTaxReportTrans_JP::findByTaxReportParameters(taxReport, TaxReportLayout2023_JP::TaxReportDataLayout).RecId));
}

public void write()
{
    super();

    if (!element.isLatestVersion() || taxReport.amendment())
    {
        element.calculateTaxReport();
        element.syncCurrentRecord();
    }
}

public void init()
{
    super();

    this.query()
        .dataSourceTable(tablenum(LedgerConsumptionTaxReportTrans_JP))
        .addRange(fieldnum(LedgerConsumptionTaxReportTrans_JP, RecId))
        .value(queryValue(LedgerConsumptionTaxReportTrans_JP::findByTaxReportParameters(taxReport, TaxReportLayout2023_JP::TaxReportAddonDataLayout).RecId));
}

void clicked()
{
    if (Box::yesNo("@GLS60095", DialogButton::No) != DialogButton::Yes)
    {
        return;
    }

    super();

    element.enableButtonsActive();

    taxReport.parmAmendment(NoYes::No);

    element.syncCurrentRecord();
}

public void clicked()
{
    super();

    taxReport = element.args().parmObject();
    taxReport.parmFileFormat(element.parmFileFormat());
    taxReport.printout();
}
