public void close()
{
    super();

    ledgerConsumptionTaxReportTrans_JP = LedgerConsumptionTaxReportTrans_JP::findByDateAndTypeOfDeclaration(
            taxReport_JP.fromDate(),
            taxReport_JP.toDate(),
            taxReport_JP.typeOfDeclaration(),
            true);
    if (ledgerConsumptionTaxReportTrans_JP
        && taxReport_JP.amendment()
        && !finalizedRep)
    {
        ttsbegin;
        ledgerConsumptionTaxReportTrans_JP.Version                = versionOrig;
        ledgerConsumptionTaxReportTrans_JP.TaxationOfficeName     = taxOffice;
        ledgerConsumptionTaxReportTrans_JP.CompanyRepresentative  = compRep;
        ledgerConsumptionTaxReportTrans_JP.AccountingPersonnel    = acctPersonnel;
        ledgerConsumptionTaxReportTrans_JP.update();
        ttscommit;
    }

    LedgerConsumptionTaxReportTrans_JP::removeVersionNotMatured(
        taxReport_JP.parmTaxPeriod(),
        taxReport_JP.parmFromDate(),
        taxReport_JP.parmToDate(),
        taxReport_JP.parmTypeOfDeclaration());
}

public SRSReportFileFormat parmFileFormat(SRSReportFileFormat _fileFormat = fileFormat)
{
    fileFormat = _fileFormat;
    return fileFormat;
}

/// <summary>
///    Enable or disable the buttons on form.
/// </summary>
void enableButtonsActive()
{
    consTaxReportTrans_JP = LedgerConsumptionTaxReportTrans_JP::findByDateAndTypeOfDeclaration(
        taxReport_JP.fromDate(),
        taxReport_JP.toDate(),
        taxReport_JP.typeOfDeclaration());
    if (consTaxReportTrans_JP.Version < TaxRepVersion::Latest)
    {
        updateAmount.enabled(true);
        finalizeTaxReports.enabled(true);
        PrintFileFormat.enabled(false);
        PrintReports.enabled(false);
    }
    else
    {
        updateAmount.enabled(false);
        finalizeTaxReports.enabled(false);
        PrintFileFormat.enabled(true);
        PrintReports.enabled(true);
    }
}

