public void init()
{
    CompanyInfo                         companyInfo;

    taxReport_JP = element.args().parmObject();

    if (!element.args().caller())
    {
        throw error("@SYS96590");
    }

    super();

    ledgerConsumptionTaxReportTrans_JP = LedgerConsumptionTaxReportTrans_JP::findByDateAndTypeOfDeclaration(
            taxReport_JP.fromDate(),
            taxReport_JP.toDate(),
            taxReport_JP.typeOfDeclaration(),
            true);
    if (ledgerConsumptionTaxReportTrans_JP)
    {
        if (taxReport_JP.parmAmendment() == NoYes::Yes)
        {
            ttsbegin;
            versionOrig     = ledgerConsumptionTaxReportTrans_JP.Version;
            taxOffice       = ledgerConsumptionTaxReportTrans_JP.TaxationOfficeName;
            compRep         = ledgerConsumptionTaxReportTrans_JP.CompanyRepresentative;
            acctPersonnel   = ledgerConsumptionTaxReportTrans_JP.AccountingPersonnel;
            companyInfo     = CompanyInfo::find();

            ledgerConsumptionTaxReportTrans_JP.initFromTaxReport(taxReport_JP);
            ledgerConsumptionTaxReportTrans_JP.update();
            ttscommit;
        }
    }
    else
    {
        taxReport_JP.createRecConsTaxReport();
    }

    element.enableButtonsActive();
    element.fieldsEnableDisable();
    element.parmFileFormat(SRSReportFileFormat::PDF);
    this.updatePrintFileformat();

    ttsbegin;
    tReportByCode = TaxReportByCode_JP::updateFromTaxReport(taxReport_JP);
    tReportByCode.update();
    ttscommit;
}

public void init()
{
    LedgerConsumptionTaxReportTrans_JP  consTaxRep;

    super();

    consTaxRep = LedgerConsumptionTaxReportTrans_JP::findByDateAndTypeOfDeclaration(
        taxReport_JP.fromDate(),
        taxReport_JP.toDate(),
        taxReport_JP.typeOfDeclaration(),
        false);

    this.query().dataSourceTable(tablenum(LedgerConsumptionTaxReportTrans_JP)).addRange(fieldnum(LedgerConsumptionTaxReportTrans_JP, RecId)).value(queryValue(consTaxRep.RecId));
}

void clicked()
{
    if (Box::yesNo("@GLS60095", DialogButton::No))
    {
        super();
        finalizedRep = true;
        element.fieldsEnableDisable();
        element.enableButtonsActive();

        taxReport_JP.parmAmendment(NoYes::No);

        element.syncCurrentRecord();
    }
}

public void clicked()
{
    super();

    taxReport_JP = element.args().parmObject();
    taxReport_JP.parmFileFormat(element.parmFileFormat());
    taxReport_JP.printout();
}
