public void init()
{
    isCashFlowInsightsActive = CashflowInsightsActivation::IsActive();

    if (isCashFlowInsightsActive)
    {
        cashFlowForecastDimensionsPanel = SysListPanel_CashFlowForecastDimensions::newForm(
            element,
            element.controlId(formcontrolstr(LedgerCovParameters, DataEntityPanel)),
            false,
            false,
            true);
    }

    super();

    if (isCashFlowInsightsActive)
    {
        DimensionsInstruction.text(strFmt("@CashManagement:CashFlowForecastFocusIntegrationKeyInstruction", DimensionAttribute::getWellKnownDimensionAttributeName(DimensionAttributeType::MainAccount)));

        this.initDimensionIntegrationList();
    }

    Dimensions.visible(isCashFlowInsightsActive);
    ExternalSource.visible(isCashFlowInsightsActive);
    this.setCashflowforecastAutomationMenuItemvisibility();

    this.setProjectVisibility();
}

private void setCashflowforecastAutomationMenuItemvisibility()
{
    if (Dynamics.AX.Application.FeatureStateProvider::isFeatureEnabled(LedgerCovTotalProcessAutomationFeature::instance()))
    {
        CalculateCashFlowForecastAutomationButton.visible(true);
        CalculateCashFlowForecastButton.visible(false);
    }
    else
    {
        CalculateCashFlowForecastAutomationButton.visible(false);
        CalculateCashFlowForecastButton.visible(true);
    }
}

