/// <summary>
/// Positions updated in the dimensions panel.
/// </summary>
public void positionsUpdated()
{
    // Ensure the hierarchy is updated when the levels change.
    this.buildDisplayString();
    DimensionHierarchy_DS.forceWrite(true);
}

/// <summary>
/// Builds the display string for the dimension integration structure.
/// </summary>
private void buildDisplayString()
{
    DefaultDimensionIntegrationStructureDisplay localDisplayString;

    str delimiter = DimensionParameters::getDimensionSegmentDelimiter();

    DimensionTopicsPrioritizer localDimensionTopicsPrioritizer = cashFlowForecastDimensionsPanel.parmDimensionTopicsPrioritizer();
    ListEnumerator listEnumerator = localDimensionTopicsPrioritizer.getTopicsEnumerator();

    while (listEnumerator.moveNext())
    {
        DimensionTopic dimensionTopic = listEnumerator.current();

        if (localDisplayString != '')
        {
            localDisplayString = localDisplayString + delimiter + dimensionTopic.getDimensionName();
        }
        else
        {
            localDisplayString = dimensionTopic.getDimensionName();
        }
    }

    displayString = localDisplayString;
}

public boolean canClose()
{
    boolean ret;

    if (!this.doAllExternalSourceHaveLegalEntitySetting())
    {
        Error(this.errorForExtSourceMissingLegalEntitySetting);
    }
    else 
    {
        ret = super();
    }

    this.resetErrorForExtSourceMissingLegalEntitySetting();

    return ret;
}

private boolean doAllExternalSourceHaveLegalEntitySetting()
{
    boolean allExternalSourceHaveLegalEntitySetting = true;

    CashFlowExternalSource extSource;
    CashFlowExternalSourceLegalEntity extSourceLE;

    while select RecId, Name from extSource
    {
        select firstonly RecId from extSourceLE
            where extSourceLE.ExternalSource == extSource.RecId;

        if (!extSourceLE)
        {
            allExternalSourceHaveLegalEntitySetting = false;
            this.errorForExtSourceMissingLegalEntitySetting = this.errorForExtSourceMissingLegalEntitySetting + extSource.Name + ', ';
        }
    }

    if (!allExternalSourceHaveLegalEntitySetting)
    {
        // Replace the last ', ' to '.'
        this.errorForExtSourceMissingLegalEntitySetting = strPoke(this.errorForExtSourceMissingLegalEntitySetting, '.', strLen(this.errorForExtSourceMissingLegalEntitySetting)-1);
    }

    return allExternalSourceHaveLegalEntitySetting;
}

private Void resetErrorForExtSourceMissingLegalEntitySetting()
{
    this.errorForExtSourceMissingLegalEntitySetting = "@CashManagement:ExternalSourceMissLegalEntitySetting";
}

