    super();

    this.setTabFocus();
}

/// <summary>
/// When args enum parameter set, set tab focus on form opening.
/// </summary>
private void setTabFocus()
{
    Args args = element.args();

    if (args && args.parmEnumType() == enumNum(LedgerCovParametersItem))
    {
        switch (args.parmEnum())
        {
            case LedgerCovParametersItem::GeneralLedger:
                GeneralLedger.setFocus();
                break;

            case LedgerCovParametersItem::ExternalSource:
                ExternalSource.setFocus();
                break;

            case LedgerCovParametersItem::Dimensions:
                Dimensions.setFocus();
                break;
        }
    }
}

