private void addRanges()
{
    QueryBuildRange queryBuildRange = this.queryBuildDataSource().addRange(fieldnum(DimensionHierarchy, StructureType));
    queryBuildRange.value(Global::queryValue(int2str(DimensionHierarchyType::CashFlowForecast)));
    queryBuildRange.status(RangeStatus::Hidden);

    queryBuildRange = this.queryBuildDataSource().addRange(fieldnum(DimensionHierarchy, DeletedVersion));
    queryBuildRange.value(queryValue(0));
    queryBuildRange.status(RangeStatus::Hidden);
}

private void findOrCreateCashFlowDimensionHierarchy()
{
    DimensionHierarchy dimHierarchy;
    select firstonly RecId from dimHierarchy
        where dimHierarchy.StructureType == DimensionHierarchyType::CashFlowForecast;

    if (!dimHierarchy.RecId)
    {
        this.create(false);
    }
}

public void create(boolean _append = false)
{
    super(_append);

    displayString = '';
    dimensionHierarchy.Name = "@CashManagement:CashFlowForecastFocusIntegrationKey";
    dimensionHierarchy.StructureType = DimensionHierarchyType::CashFlowForecast;
}

public void linkActive()
{
    // Do not call super() because linking is done manually.
}

public int active()
{
    int returnValue;

    if (!isCashFlowInsightsActive || isWriting)
    {
        // When a button is clicked, active gets called during the write's super() call, so we must skip active due to code that must run post-super in write first
        activeSkippedDuringWrite = true;
        return returnValue;
    }

    returnValue = super();

    this.initializeDimensionsPanel();
    this.retrieveDisplayString();

    return returnValue;
}

private void initializeDimensionsPanel()
{
    // Create a prioritizer which tracks current selections for this hierarchy
    dimensionTopicsPrioritizer = DimensionTopicsPrioritizer::construct(dimensionHierarchy);
    originalDimensionTopicsPrioritizer = DimensionTopicsPrioritizer::construct(dimensionHierarchy);

    // Build up the list of all available values
    Set dimAttributeNames = new Set(Types::String);
    DimensionAttributeCollection dimensionAttributeCollection = DimensionHierarchy::getAttributesNotInHierarchy(DimensionHierarchy);
    dimensionAttributeCollection.initializeEnumerator();

    while (dimensionAttributeCollection.moveNext())
    {
        DimensionAttribute dimAttr = DimensionAttribute::find(dimensionAttributeCollection.current());
        dimAttributeNames.add(dimAttr.Name);
    }

    // Fill the list panel
    cashFlowForecastDimensionsPanel.parmAllowedDimensionAttributes(dimAttributeNames);
    cashFlowForecastDimensionsPanel.parmDimensionTopicsPrioritizer(dimensionTopicsPrioritizer);
    cashFlowForecastDimensionsPanel.fill();  
}

private void retrieveDisplayString()
{
    DimensionHierarchyIntegration dimensionHierarchyIntegration;
    select firstonly dimensionHierarchyIntegration
        where dimensionHierarchyIntegration.DimensionHierarchy == dimensionHierarchy.RecId;

    displayString = dimensionHierarchyIntegration ? dimensionHierarchyIntegration.DisplayString : '';
}

public void write()
{
    // Pull data from the current prioritizer
    if (dimensionTopicsPrioritizer == null || originalDimensionTopicsPrioritizer == null)
    {
        dimensionTopicsPrioritizer = DimensionTopicsPrioritizer::construct(dimensionHierarchy);
        originalDimensionTopicsPrioritizer = DimensionTopicsPrioritizer::construct(dimensionHierarchy);
    }

    ListEnumerator selectedDimAttributeEnumerator = dimensionTopicsPrioritizer.getTopicsEnumerator();
    ListEnumerator originalDimAttributeEnumerator = originalDimensionTopicsPrioritizer.getTopicsEnumerator();

    boolean changesMadeToElements = dimensionTopicsPrioritizer.elements() != originalDimensionTopicsPrioritizer.elements();
    boolean isOrderChanged = false;
    isWriting = true;

    if (!changesMadeToElements)
    {
        // See if all current topics exist in the hierarchy
        while (selectedDimAttributeEnumerator.moveNext() && originalDimAttributeEnumerator.moveNext())
        {
            DimensionTopic dimensionTopic = selectedDimAttributeEnumerator.current();
            DimensionTopic originalDimensionTopic = originalDimAttributeEnumerator.current();

            if (dimensionTopic.parmDimensionAttributeId() != originalDimensionTopic.parmDimensionAttributeId())
            {
                isOrderChanged = true;
            }

            DimensionHierarchyLevel dimHierarchyLevel;
            select firstonly RecId from dimHierarchyLevel
                where dimHierarchyLevel.DimensionHierarchy == DimensionHierarchy.RecId
                    && dimHierarchyLevel.DimensionAttribute == dimensionTopic.parmDimensionAttributeId();

            if (!dimHierarchyLevel)
            {
                // changesMade is a superset of isOrderChanged so we can stop
                changesMadeToElements = true;
                break;
            }
        }
    }

    boolean isNew = (dimensionHierarchy.RecId == 0);

    super();

    // If changes have been made, verify the user intended to make changes.
    // If it is the first save (isNew) then always save the changes; else confirm with the user. 
    if (isNew ||
        ((changesMadeToElements || isOrderChanged)
            && Box::yesNo(strFmt("@CashManagement:LedgerCovChangeDimensionIntegrationKeyConfirmationMessage", dimensionHierarchy.Name), DialogButton::Yes, "@Dimension:IntegrationStructure") != DialogButton::No))
    {
        isWriting = false;

        selectedDimAttributeEnumerator.reset();

        ttsbegin;

        // Clear out existing hierarchy levels
        DimensionHierarchyLevel dimHierarchyLevel;
        delete_from dimHierarchyLevel
            where dimHierarchyLevel.DimensionHierarchy == dimensionHierarchy.RecId;

        // Populate the updated hierarchy levels
        Level level = 0;
        while (selectedDimAttributeEnumerator.moveNext())
        {
            DimensionTopic dimensionTopic = selectedDimAttributeEnumerator.current();

            if (dimensionTopic.parmDimensionAttributeId() != DimensionAttribute::getWellKnownDimensionAttribute(DimensionAttributeType::MainAccount))
            {
                level++;
                dimHierarchyLevel.clear();
                dimHierarchyLevel.DimensionHierarchy = dimensionHierarchy.RecId;
                dimHierarchyLevel.DimensionAttribute = dimensionTopic.parmDimensionAttributeId();
                dimHierarchyLevel.Level = level;
                dimHierarchyLevel.insert();
            }
        }

        ttscommit;
    }

    ttsbegin;

    DimensionHierarchyIntegration dimensionHierarchyIntegrationToBeUpdated;
    select firstonly forupdate dimensionHierarchyIntegrationToBeUpdated
        where dimensionHierarchyIntegrationToBeUpdated.DimensionHierarchy == dimensionHierarchy.RecId;

    dimensionHierarchyIntegrationToBeUpdated.DimensionHierarchy = dimensionHierarchy.RecId;
    dimensionHierarchyIntegrationToBeUpdated.DisplayString = displayString;
    dimensionHierarchyIntegrationToBeUpdated.IsDefault = true;

    if (!dimensionHierarchyIntegrationToBeUpdated.validateWrite() ||
        !DimensionHierarchyIntegration::validateIntegrationForHierarchy(dimensionHierarchy.RecId))
    {
        ttsAbort;
        return;
    }

    if (isNew || !dimensionHierarchyIntegrationToBeUpdated)
    {
        dimensionHierarchyIntegrationToBeUpdated.insert();
    }
    else
    {
        dimensionHierarchyIntegrationToBeUpdated.update();
    }

    ttscommit;

    isWriting = false;

    DimensionHierarchy_DS.research(true);

    if (activeSkippedDuringWrite)
    {
        // Manually call active() after write() completes since it was skipped earlier
        activeSkippedDuringWrite = false;
        DimensionHierarchy_ds.active();
    }
}

