public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    MainAccount resolvedRecord;

    if (_formReferenceControl)
    {
        LedgerLiquidity liquidity;
        MainAccountNum mainAccountId = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(MainAccount, MainAccountId), tableStr(MainAccount))).value();

        changecompany(CompanyInfo::findRecId(CashFlowExternalSourceLegalEntity.LegalEntity).DataArea)
        {
            select firstonly resolvedRecord
            exists join liquidity
                where liquidity.MainAccount == resolvedRecord.RecId
                    && resolvedRecord.MainAccountId == mainAccountId;
        }
    }

    return resolvedRecord;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(MainAccount), _formReferenceControl, true);

    // Display the MainAccountID and Name fields in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(MainAccount, MainAccountId));
    sysTableLookup.addLookupMethod(tableMethodStr(MainAccount, localizedName));

    CompanyInfo company = CompanyInfo::findRecId(CashFlowExternalSourceLegalEntity.LegalEntity);
    MainAccount selectedRecord;

    // Create a custom Query
    Query lookupQuery = new Query();
    lookupQuery.allowCrossCompany(true);

    QueryBuildDataSource queryDS = lookupQuery.addDataSource(tableNum(MainAccount));

    QueryBuildDataSource ledgerChartOfAccountDS = queryDS.addDataSource(tableNum(LedgerChartOfAccounts));
    ledgerChartOfAccountDS.joinMode(JoinMode::InnerJoin);
    ledgerChartOfAccountDS.addLink(fieldNum(MainAccount, LedgerChartOfAccounts), fieldNum(LedgerChartOfAccounts, RecId));

    QueryBuildDataSource ledgerDS = ledgerChartOfAccountDS.addDataSource(tableNum(Ledger));
    ledgerDS.joinMode(JoinMode::InnerJoin);
    ledgerDS.addLink(fieldNum(LedgerChartOfAccounts, RecId), fieldNum(Ledger, ChartOfAccounts));

    QueryBuildDataSource ledgerLiquidityDS = queryDS.addDataSource(tableNum(LedgerLiquidity));
    ledgerLiquidityDS.joinMode(JoinMode::InnerJoin);
    ledgerLiquidityDS.addLink(fieldNum(MainAccount, RecId), FieldNum(LedgerLiquidity, MainAccount));

    QueryBuildDataSource companyDS = ledgerDS.addDataSource(tableNum(CompanyInfo));
    companyDS.joinMode(JoinMode::InnerJoin);
    companyDS.addLink(fieldNum(Ledger, PrimaryForLegalEntity), fieldNum(CompanyInfo, RecId));
    companyDS.addLink(fieldNum(LedgerLiquidity, DataAreaId), fieldNum(CompanyInfo, DataArea), ledgerLiquidityDS.name());
    companyDS.addRange(fieldNum(CompanyInfo, DataArea)).value(company.DataArea);

    sysTableLookup.parmQuery(lookupQuery);

    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

