public void clicked()
{
    VendGroup vendGroupLocal = VendGroup_ds.getFirst(true);
    while (vendGroupLocal)
    {
        ttsbegin;
        VendGroup vendGroupUpdate = VendGroup::find(vendGroupLocal.VendGroup, true);
        vendGroupUpdate.ClearingPeriod = '';
        vendGroupUpdate.update();
        ttscommit;

        vendGroupLocal = VendGroup_ds.getNext();
    }

    VendGroup_ds.executeQuery();
}

public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(
        tableNum(VendGroup),
        this,
        true);

    sysTableLookup.addLookupfield(fieldNum(VendGroup, VendGroup));
    sysTableLookup.addLookupfield(fieldNum(VendGroup, Name));

    Query lookupQuery = new Query();
    QueryBuildDataSource tableQbds = SysQuery::findOrCreateDataSource(lookupQuery, tableNum(VendGroup));
    QueryBuildRange qbr = SysQuery::findOrCreateRange(tableQbds, fieldNum(VendGroup, ClearingPeriod));
    qbr.value(SysQuery::valueEmptyString());

    sysTableLookup.parmQuery(lookupQuery);

    sysTableLookup.performFormLookup();
}

