public void clicked()
{
    if (vendLedgerAccounts.RecId)
    {
        ttsbegin;
        VendLedgerAccounts vendLedgerAccountsUpdate = VendLedgerAccounts::find(
            vendLedgerAccounts.PostingProfile,
            vendLedgerAccounts.AccountCode,
            vendLedgerAccounts.Num,
            true);

        vendLedgerAccountsUpdate.ClearingLedgerDimension = 0;
        vendLedgerAccountsUpdate.update();
        ttscommit;
    }

    vendLedgerAccounts_ds.executeQuery();
}

public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(VendLedgerAccounts), this, true);

    sysTableLookup.addLookupfield(fieldNum(VendLedgerAccounts, PostingProfile));
    sysTableLookup.addLookupfield(fieldNum(VendLedgerAccounts, AccountCode));
    sysTableLookup.addLookupfield(fieldNum(VendLedgerAccounts, Num));
    sysTableLookup.addLookupfield(fieldNum(VendLedgerAccounts, RecId), true);

    Query lookupQuery = new Query();
    QueryBuildDataSource tableQbds = SysQuery::findOrCreateDataSource(lookupQuery, tableNum(VendLedgerAccounts));
    QueryBuildRange qbr = SysQuery::findOrCreateRange(tableQbds, fieldNum(VendLedgerAccounts, ClearingLedgerDimension));
    qbr.value(SysQuery::value(0));

