}

public boolean modified()
{
    if (!vendLedgerAccounts.RecId)
    {
        RecId recId = str2Int64(VendLedgerAccounts_PostingProfile.text());

        VendLedgerAccounts vendLedgerAccountsFind;
        select PostingProfile, AccountCode, Num from vendLedgerAccountsFind
            where vendLedgerAccountsFind.RecId == recId;

        vendLedgerAccounts.PostingProfile = vendLedgerAccountsFind.PostingProfile;
        VendLedgerAccounts_PostingProfile.text(vendLedgerAccountsFind.PostingProfile);

        VendLedgerAccounts_AccountCode.item(vendLedgerAccountsFind.AccountCode);
        VendLedgerAccounts_AccountCode.text(enum2Str(vendLedgerAccountsFind.AccountCode));
        vendLedgerAccounts.AccountCode = vendLedgerAccountsFind.AccountCode;

        VendLedgerAccounts_Num.text(vendLedgerAccountsFind.Num);
        vendLedgerAccounts.Num = vendLedgerAccountsFind.Num;

        VendLedgerAccounts_RecId.value(recid);
    }

    return super();
}

public void clicked()
{
    CustGroup custGroupLocal = CustGroup_ds.getFirst(true);
    while (custGroupLocal)
    {
        ttsbegin;
        CustGroup custGroupUpdate = CustGroup::find(custGroupLocal.CustGroup, true);
        custGroupUpdate.ClearingPeriod = '';
        custGroupUpdate.update();
        ttscommit;

        custGroupLocal = CustGroup_ds.getNext();
    }

    CustGroup_ds.executeQuery();
}

public void lookup()
{
    SysTableLookup sysTableLookup = SysTableLookup::newParameters(
        tableNum(CustGroup),
        this,
        true);

