Method.Source:
    [Form]
    public class LedgerCurrencyParameters_RU extends FormRun
    {
        Ledger                                      ledger;
        boolean                                     ledgerMode;
        boolean                                     isExtendedSetup;

        #define.general(0)
        #define.cust(1)
        #define.vend(2)
        #define.empl(3)
}

public void fillLedgerDimensionGrids()
{
    element.populateTmpTables(ledgerId.value(), ledgerMode ? '' : currency.CurrencyCode);

    tmpGeneral_DS.executeQuery();
    tmpAmountDiff_DS.executeQuery();
    tmpCust_DS.executeQuery();
    tmpVend_DS.executeQuery();
    tmpEmpl_ds.executeQuery();
    ledgerCurrencyParameters_RU_ds.executeQuery();
    currency_ds.refresh();
}

public void init()
{
    if (element.args() && element.args().dataset() == tableNum(Ledger))
    {
        ledgerMode  = true;
        ledger      = Ledger::find(element.args().record().RecId);
    }

    isExtendedSetup = LedgerParameters::find().ExchRateDiffCalcType_W != ExchRateDiffCalcType_W::Standard;

    super();

    element.setupLedgerCurrencyMode();
}

public void populateTmpTables(RefRecId _ledger, CurrencyCode _currencyCode = '')
{
    CurrencyLedgerGainLossAccount   currencyLedgerGainLossAccount;
    LedgerGainLossAccount           ledgerGainLossAccount;
    Map                             generalMap;
    Map                             amountDiffMap;
    Map                             custMap;
    Map                             vendMap;
    Map                             emplMap;
    LedgerCurrencyParameters_RU     localLedgerCurrencyParameters;

    void add2map(
        Map                         _accountsMap,
        CurrencyGainLossAccountType _type,
        recId                       _ledgerDimension                = 0,
        ExchAdjTaxableType_W        _exchAdjTaxable                 = ExchAdjTaxableType_W::No,
        recId                       _taxLedgerDimension             = 0,
        recId                       _currencyLedgerGainLossAccount  = 0)
    {
        TmpCurrencyLedgerGainLossAccount localAccount;

        if (_accountsMap.exists(_type))
        {
            localAccount = _accountsMap.lookup(_type);
        }

        localAccount.CurrencyLedgerGainLossAccount  = _currencyLedgerGainLossAccount;
        localAccount.CurrencyCode                   = _currencyCode;
        localAccount.Ledger                         = _ledger;
        localAccount.AccountType                    = _type;
        localAccount.LedgerDimension                = _ledgerDimension;
        localAccount.ExchAdjTaxable_RU              = _exchAdjTaxable;
        localAccount.TaxLedgerDimension_RU          = _taxLedgerDimension;

        element.updateAccountName(localAccount);
        _accountsMap.insert(_type, localAccount);
    }

    void createRecords(Map _map, TmpCurrencyLedgerGainLossAccount _accounts)
    {
        MapEnumerator me;

        if (_map)
        {
            me = _map.getEnumerator();
            while (me.moveNext())
            {
                _accounts.data(me.currentValue());
                _accounts.insert();
            }
        }
    }

    delete_from tmpGeneral;
    delete_from tmpCust;
    delete_from tmpVend;
    delete_from tmpAmountDiff;
    delete_from tmpEmpl;

    if (!ledgerMode && _currencyCode)
    {
        localLedgerCurrencyParameters = LedgerCurrencyParameters_RU::findCurrencyCode(_ledger, _currencyCode, true);
    }

    if (!localLedgerCurrencyParameters)
    {
        localLedgerCurrencyParameters = LedgerCurrencyParameters_RU::findOrCreateLedger(_ledger);
    }

    details.allowEdit(ledgerMode || (_currencyCode && localLedgerCurrencyParameters.CurrencyCode == _currencyCode));

    findOrCreateRange_W(
        ledgerCurrencyParameters_RU_ds.query().dataSourceTable(tableNum(LedgerCurrencyParameters_RU)),
        fieldNum(LedgerCurrencyParameters_RU, RecId),
        SysQuery::value(localLedgerCurrencyParameters.RecId));

    if (_ledger == 0)
    {
        return;
    }

    ledger = Ledger::find(_ledger);

    TmpGeneral_LedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);
    TmpAmountDiff_LedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);
    TmpCust_LedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);
    TmpVend_LedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);
    TmpCust_TaxLedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);
    TmpVend_TaxLedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);
    TmpEmpl_LedgerDimension.parmCurrentLedgerCOA(ledger.ChartOfAccounts);

    // Genreal
    generalMap = new Map(Types::Enum, Types::Record);
    add2map(generalMap, CurrencyGainLossAccountType::RealizedGain);
    add2map(generalMap, CurrencyGainLossAccountType::UnrealizedGain);
    add2map(generalMap, CurrencyGainLossAccountType::RealizedLoss);
    add2map(generalMap, CurrencyGainLossAccountType::UnrealizedLoss);

    while select ledgerGainLossAccount
        where ledgerGainLossAccount.Ledger == _ledger
    {
        add2map(
            generalMap,
            ledgerGainLossAccount.AccountType,
            ledgerGainLossAccount.LedgerDimension,
            ExchAdjTaxableType_W::No,
            0,
            ledgerMode ? ledgerGainLossAccount.RecId : 0);
    }

    if (ledgerMode)
    {
        amountDiffMap = new Map(Types::Enum, Types::Record);
        add2map(amountDiffMap, CurrencyGainLossAccountType::TaxAmountDiffGain_RU,   localLedgerCurrencyParameters.TaxAmountDiffProfitLedgerDimension);
        add2map(amountDiffMap, CurrencyGainLossAccountType::TaxAmountDiffLoss_RU,   localLedgerCurrencyParameters.TaxAmountDiffLossLedgerDimension);
        add2map(amountDiffMap, CurrencyGainLossAccountType::TaxAmountDiffOffset_RU, localLedgerCurrencyParameters.TaxAmountDiffLedgerDimension);
    }
    else
    {
        while select currencyLedgerGainLossAccount
            where currencyLedgerGainLossAccount.CurrencyCode    == _currencyCode
               && currencyLedgerGainLossAccount.Ledger          == _ledger
        {
            add2map(
                generalMap,
                currencyLedgerGainLossAccount.AccountType,
                currencyLedgerGainLossAccount.LedgerDimension,
                ExchAdjTaxableType_W::No,
                0,
                currencyLedgerGainLossAccount.RecId);
        }
    }

    if (isExtendedSetup)
    {
        // Customer
        custMap = new Map(Types::Enum, Types::Record);
        add2map(
            custMap,
            CurrencyGainLossAccountType::RealizedGain,
            localLedgerCurrencyParameters.RealizedProfitCustLedgerDimension,
            localLedgerCurrencyParameters.ExchRatePos,
            localLedgerCurrencyParameters.RealizedProfitTaxCustLedgerDimension);

        add2map(
            custMap,
            CurrencyGainLossAccountType::RealizedLoss,
            localLedgerCurrencyParameters.RealizedLossCustLedgerDimension,
            localLedgerCurrencyParameters.ExchRateNeg,
            localLedgerCurrencyParameters.RealizedLossTaxCustLedgerDimension);

        if (ledgerMode)
        {
            add2map(
                custMap,
                CurrencyGainLossAccountType::UnrealizedGain,
                localLedgerCurrencyParameters.UnrealizedProfitCustLedgerDimension,
                localLedgerCurrencyParameters.ExchRateNonrealProfitTaxCust,
                localLedgerCurrencyParameters.UnrealizedProfitTaxCustLedgerDimension);

            add2map(
                custMap,
                CurrencyGainLossAccountType::UnrealizedLoss,
                localLedgerCurrencyParameters.UnrealizedLossCustLedgerDimension,
                localLedgerCurrencyParameters.ExchRateNonrealLossTaxCust,
                localLedgerCurrencyParameters.UnrealizedLossTaxCustLedgerDimension);
        }
        else
        {
            add2map(
                custMap,
                CurrencyGainLossAccountType::UnrealizedGain,
                localLedgerCurrencyParameters.UnrealizedProfitCustLedgerDimension);

            add2map(
                custMap,
                CurrencyGainLossAccountType::UnrealizedLoss,
                localLedgerCurrencyParameters.UnrealizedLossCustLedgerDimension);
        }

        // Vendor
        vendMap = new Map(Types::Enum, Types::Record);
        add2map(
            vendMap,
            CurrencyGainLossAccountType::RealizedGain,
            localLedgerCurrencyParameters.RealizedProfitVendLedgerDimension,
            localLedgerCurrencyParameters.ExchRateProfitTaxVend,
            localLedgerCurrencyParameters.RealizedProfitTaxVendLedgerDimension);

        add2map(
            vendMap,
            CurrencyGainLossAccountType::RealizedLoss,
            localLedgerCurrencyParameters.RealizedLossVendLedgerDimension,
            localLedgerCurrencyParameters.ExchRateLossTaxVend,
            localLedgerCurrencyParameters.RealizedLossTaxVendLedgerDimension);

        add2map(vendMap, CurrencyGainLossAccountType::UnrealizedGain,       localLedgerCurrencyParameters.UnrealizedProfitVendLedgerDimension);
        add2map(vendMap, CurrencyGainLossAccountType::UnrealizedLoss,       localLedgerCurrencyParameters.UnrealizedLossVendLedgerDimension);
        add2map(vendMap, CurrencyGainLossAccountType::ConversionGain_RU,    localLedgerCurrencyParameters.CurConvertProfitLedgerDimension);
        add2map(vendMap, CurrencyGainLossAccountType::ConversionLoss_RU,    localLedgerCurrencyParameters.CurConvertLossLedgerDimension);

        // Empl
        emplMap = new Map(Types::Enum, Types::Record);
        add2map(emplMap, CurrencyGainLossAccountType::RealizedGain,     localLedgerCurrencyParameters.RealizedProfitEmplLedgerDimension);
        add2map(emplMap, CurrencyGainLossAccountType::RealizedLoss,     localLedgerCurrencyParameters.RealizedLossEmplLedgerDimension);
        add2map(emplMap, CurrencyGainLossAccountType::UnrealizedGain,   localLedgerCurrencyParameters.UnrealizedProfitEmplLedgerDimension);
        add2map(emplMap, CurrencyGainLossAccountType::UnrealizedLoss,   localLedgerCurrencyParameters.UnrealizedLossEmplLedgerDimension);

        createRecords(custMap, tmpCust);
        createRecords(vendMap, tmpVend);
        createRecords(emplMap, tmpEmpl);
    }
    else
    {
        add2map(generalMap, CurrencyGainLossAccountType::ConversionGain_RU,    localLedgerCurrencyParameters.CurConvertProfitLedgerDimension);
        add2map(generalMap, CurrencyGainLossAccountType::ConversionLoss_RU,    localLedgerCurrencyParameters.CurConvertLossLedgerDimension);
    }

    createRecords(generalMap,       tmpGeneral);
    createRecords(amountDiffMap,    tmpAmountDiff);
}

private void setupLedgerCurrencyMode()
{
    ledgerId.allowEdit(!ledgerMode);

    if (ledgerMode)
    {
        ledgerId.value(ledger.RecId);
        gridContainer.visible(false);
        vSplitter.visible(false);
        detailsCurrencyHeader.visible(false);
        taxAmountDifferenceGrid.visible(true);
    }
    else
    {
        ledgerId.value(Ledger::current());
    }

    exchDiffCust.visible(isExtendedSetup);
    exchDiffVend.visible(isExtendedSetup);
    exchDiffEmpl.visible(isExtendedSetup);
    exchRateTax.visible(!isExtendedSetup);
    taxDimensionConvertGeneral.visible(!isExtendedSetup);

    if (!isExtendedSetup)
    {
        generalGrid.visibleRows(8);
    }
}

public void updateAccountName(TmpCurrencyLedgerGainLossAccount _account)
{
    if (_account.LedgerDimension)
    {
        _account.AccountName =
            MainAccount::getLocalizedNameByMainAccountId(
                LedgerDimensionFacade::getMainAccountIdFromLedgerDimension(_account.LedgerDimension), ledger.ChartOfAccounts);
    }
    else
    {
        _account.AccountName = '';
    }

    if (_account.TaxLedgerDimension_RU)
    {
        _account.TaxAccountName_RU =
            MainAccount::getLocalizedNameByMainAccountId(
                LedgerDimensionFacade::getMainAccountIdFromLedgerDimension(_account.TaxLedgerDimension_RU), ledger.ChartOfAccounts);
    }
    else
    {
        _account.TaxAccountName_RU = '';
    }
}

public void updateLedgerDimensionValue(
    TmpCurrencyLedgerGainLossAccount    _account,
    fieldId                             _ledgerDimensionfieldId,
    fieldId                             _taxLedgerDimensionfieldId  = 0,
    fieldId                             _exchAdjTaxableFieldId      = 0)
{
    boolean parmsModified;

    if (_account.orig().LedgerDimension != _account.LedgerDimension)
    {
        LedgerCurrencyParameters_RU::updateLedgerDimensionValue(
            _account.Ledger,
            _account.CurrencyCode,
            _ledgerDimensionfieldId,
            _account.LedgerDimension);

        parmsModified = true;
    }

    if ((_taxLedgerDimensionfieldId && _account.orig().TaxLedgerDimension_RU != _account.TaxLedgerDimension_RU) ||
        (_exchAdjTaxableFieldId     && _account.orig().ExchAdjTaxable_RU     != _account.ExchAdjTaxable_RU))
    {
        LedgerCurrencyParameters_RU::updateLedgerDimensionValue(
            _account.Ledger,
            _account.CurrencyCode,
            _taxLedgerDimensionfieldId,
            _account.TaxLedgerDimension_RU,
            _exchAdjTaxableFieldId,
            _account.ExchAdjTaxable_RU);

        parmsModified = true;
    }

    if (parmsModified)
    {
        ledgerCurrencyParameters_RU_ds.reread();
        ledgerCurrencyParameters_RU_ds.refresh();
    }
}

public void writeTmpAccount(TmpCurrencyLedgerGainLossAccount _account, int _type)
{
    CurrencyLedgerGainLossAccount   currencyLedgerAccount;
    LedgerGainLossAccount           ledgerGainLossAccount;

    switch (_type)
    {
        case #general:
            if (_account.CurrencyLedgerGainLossAccount)
            {
                if (_account.LedgerDimension)
                {
                    if (ledgerMode)
                    {
                        update_recordset ledgerGainLossAccount setting LedgerDimension = _account.LedgerDimension
                            where ledgerGainLossAccount.RecId == _account.CurrencyLedgerGainLossAccount;
                    }
                    else
                    {
                        update_recordset currencyLedgerAccount setting LedgerDimension = _account.LedgerDimension
                            where currencyLedgerAccount.RecId == _account.CurrencyLedgerGainLossAccount;
                    }
                }
                else
                {
                    if (ledgerMode)
                    {
                        delete_from ledgerGainLossAccount
                            where ledgerGainLossAccount.RecId == _account.CurrencyLedgerGainLossAccount;
                    }
                    else
                    {
                        delete_from currencyLedgerAccount
                            where currencyLedgerAccount.RecId == _account.CurrencyLedgerGainLossAccount;
                    }

                    _account.CurrencyLedgerGainLossAccount = 0;
                }
            }
            else
            {
                switch (_account.AccountType)
                {
                    case CurrencyGainLossAccountType::RealizedGain,
                         CurrencyGainLossAccountType::UnrealizedGain,
                         CurrencyGainLossAccountType::RealizedLoss,
                         CurrencyGainLossAccountType::UnrealizedLoss:

                        if (ledgerMode)
                        {
                            ledgerGainLossAccount.clear();
                            ledgerGainLossAccount.AccountType       = _account.AccountType;
                            ledgerGainLossAccount.LedgerDimension   = _account.LedgerDimension;
                            ledgerGainLossAccount.Ledger            = _account.Ledger;
                            ledgerGainLossAccount.insert();

                            _account.CurrencyLedgerGainLossAccount = ledgerGainLossAccount.RecId;
                        }
                        else
                        {
                            currencyLedgerAccount.clear();
                            currencyLedgerAccount.AccountType       = _account.AccountType;
                            currencyLedgerAccount.LedgerDimension   = _account.LedgerDimension;
                            currencyLedgerAccount.CurrencyCode      = _account.CurrencyCode;
                            currencyLedgerAccount.Ledger            = _account.Ledger;
                            currencyLedgerAccount.insert();

                            _account.CurrencyLedgerGainLossAccount = currencyLedgerAccount.RecId;
                        }
                        break;

                    case CurrencyGainLossAccountType::TaxAmountDiffGain_RU:
                        element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, TaxAmountDiffProfitLedgerDimension));
                        break;

                    case CurrencyGainLossAccountType::TaxAmountDiffLoss_RU:
                        element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, TaxAmountDiffLossLedgerDimension));
                        break;

                    case CurrencyGainLossAccountType::TaxAmountDiffOffset_RU:
                        element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, TaxAmountDiffLedgerDimension));
                        break;

                    case CurrencyGainLossAccountType::ConversionGain_RU:
                        element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, CurConvertProfitLedgerDimension));
                        break;

                    case CurrencyGainLossAccountType::ConversionLoss_RU:
                        element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, CurConvertLossLedgerDimension));
                        break;
                }
            }
            break;

        case #cust:
            switch (_account.AccountType)
            {
                case CurrencyGainLossAccountType::RealizedGain:
                    element.updateLedgerDimensionValue(
                        _account,
                        fieldNum(LedgerCurrencyParameters_RU, RealizedProfitCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, RealizedProfitTaxCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, ExchRatePos));
                    break;

                case CurrencyGainLossAccountType::UnrealizedGain:
                    element.updateLedgerDimensionValue(
                        _account,
                        fieldNum(LedgerCurrencyParameters_RU, UnrealizedProfitCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, UnrealizedProfitTaxCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, ExchRateNonrealProfitTaxCust));
                    break;

                case CurrencyGainLossAccountType::RealizedLoss:
                    element.updateLedgerDimensionValue(
                        _account,
                        fieldNum(LedgerCurrencyParameters_RU, RealizedLossCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, RealizedLossTaxCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, ExchRateNeg));
                    break;

                case CurrencyGainLossAccountType::UnrealizedLoss:
                    element.updateLedgerDimensionValue(
                        _account,
                        fieldNum(LedgerCurrencyParameters_RU, UnrealizedLossCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, UnrealizedLossTaxCustLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, ExchRateNonrealLossTaxCust));
                    break;
            }
            break;

        case #vend:
            switch (_account.AccountType)
            {
                case CurrencyGainLossAccountType::RealizedGain:
                    element.updateLedgerDimensionValue(
                        _account,
                        fieldNum(LedgerCurrencyParameters_RU, RealizedProfitVendLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, RealizedProfitTaxVendLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, ExchRateProfitTaxVend));
                    break;

                case CurrencyGainLossAccountType::UnrealizedGain:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, UnrealizedProfitVendLedgerDimension));
                    break;

                case CurrencyGainLossAccountType::RealizedLoss:
                    element.updateLedgerDimensionValue(
                        _account,
                        fieldNum(LedgerCurrencyParameters_RU, RealizedLossVendLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, RealizedLossTaxVendLedgerDimension),
                        fieldNum(LedgerCurrencyParameters_RU, ExchRateLossTaxVend));
                    break;

                case CurrencyGainLossAccountType::UnrealizedLoss:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, UnrealizedLossVendLedgerDimension));
                    break;

                case CurrencyGainLossAccountType::ConversionGain_RU:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, CurConvertProfitLedgerDimension));
                    break;

                case CurrencyGainLossAccountType::ConversionLoss_RU:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, CurConvertLossLedgerDimension));
                    break;
            }
            break;

        case #empl:
            switch (_account.AccountType)
            {
                case CurrencyGainLossAccountType::RealizedGain:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, RealizedProfitEmplLedgerDimension));
                    break;

                case CurrencyGainLossAccountType::UnrealizedGain:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, UnrealizedProfitEmplLedgerDimension));
                    break;

                case CurrencyGainLossAccountType::RealizedLoss:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, RealizedLossEmplLedgerDimension));
                    break;

                case CurrencyGainLossAccountType::UnrealizedLoss:
                    element.updateLedgerDimensionValue(_account, fieldNum(LedgerCurrencyParameters_RU, UnrealizedLossEmplLedgerDimension));
                    break;
            }
            break;
    }

    element.updateAccountName(_account);
}

public void init()
{
    super();
    this.cacheAddMethod('editActivateParameters');
}

public edit NoYes editActivateParameters(boolean _edit, Currency _currency, NoYes _value)
{
    LedgerCurrencyParameters_RU     ledgerCurrencyParameters;
    CurrencyLedgerGainLossAccount   currencyLedgerGainLossAccount;

    if (_edit && _currency.CurrencyCode)
    {
        if (_value)
        {
            if (_currency.CurrencyCode && _currency.CurrencyCode == ledger.AccountingCurrency)
            {
                throw error("@GLS100001");
            }

            ttsbegin;
            ledgerCurrencyParameters = LedgerCurrencyParameters_RU::findLedger(ledger.RecId, true);
            ledgerCurrencyParameters.CurrencyCode = _currency.CurrencyCode;
            ledgerCurrencyParameters.insert();
            ttscommit;
            element.fillLedgerDimensionGrids();
        }
        else
        {
            if (Box::yesNo(strFmt("@GLS116087", _currency.CurrencyCode), DialogButton::No) == DialogButton::Yes)
            {
                ttsbegin;

                delete_from ledgerCurrencyParameters
                    where ledgerCurrencyParameters.Ledger       == ledger.RecId
                       && ledgerCurrencyParameters.CurrencyCode == _currency.CurrencyCode;

                delete_from currencyLedgerGainLossAccount
                    where currencyLedgerGainLossAccount.Ledger          == ledger.RecId
                       && currencyLedgerGainLossAccount.CurrencyCode    == _currency.CurrencyCode;

                ttscommit;

                element.fillLedgerDimensionGrids();
            }
        }
    }

    return LedgerCurrencyParameters_RU::findCurrencyCode(ledger.RecId, _currency.CurrencyCode).CurrencyCode == _currency.CurrencyCode;
}

