public int active()
{
    int ret = super();

    tmpGeneral_LedgerDimension.helpText(strFmt("@SYS313297", tmpGeneral.AccountType));

    return ret;
}

public int active()
{
    int ret = super();

    tmpCust_LedgerDimension.helpText(strFmt("@SYS313297", tmpCust.AccountType));

    if (tmpCust.AccountType == CurrencyGainLossAccountType::RealizedGain    ||
        tmpCust.AccountType == CurrencyGainLossAccountType::RealizedLoss    ||
      ((tmpCust.AccountType == CurrencyGainLossAccountType::UnrealizedGain  ||
        tmpCust.AccountType == CurrencyGainLossAccountType::UnrealizedLoss) &&
        ledgerMode))
    {
        tmpCust_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, ExchAdjTaxable_RU)).allowEdit(true);
        tmpCust_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(tmpCust.ExchAdjTaxable_RU != ExchAdjTaxableType_W::No);
    }
    else
    {
        tmpCust_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, ExchAdjTaxable_RU)).allowEdit(false);
        tmpCust_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(false);
    }

    return ret;
}

public void write()
{
    ttsbegin;
    element.writeTmpAccount(tmpCust, #cust);

    super();
    ttscommit;
}

public void modified()
{
    super();

    if (tmpCust.ExchAdjTaxable_RU == ExchAdjTaxableType_W::No)
    {
        tmpCust.TaxLedgerDimension_RU   = 0;
        tmpCust.TaxAccountName_RU       = '';
        tmpCust_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(false);
        tmpCust_ds.rereadReferenceDataSources();
    }
    else
    {
        tmpCust_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(true);
    }
}

public void write()
{
    ttsbegin;
    element.writeTmpAccount(tmpVend, #vend);

    super();
    ttscommit;
}

public int active()
{
    int ret = super();

    tmpVend_LedgerDimension.helpText(strFmt("@SYS313297", tmpVend.AccountType));

    if (tmpVend.AccountType == CurrencyGainLossAccountType::RealizedGain ||
        tmpVend.AccountType == CurrencyGainLossAccountType::RealizedLoss)
    {
        tmpVend_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, ExchAdjTaxable_RU)).allowEdit(true);
        tmpVend_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(tmpCust.ExchAdjTaxable_RU != ExchAdjTaxableType_W::No);
    }
    else
    {
        tmpVend_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, ExchAdjTaxable_RU)).allowEdit(false);
        tmpVend_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(false);
    }

    return ret;
}

public void modified()
{
    super();

    if (tmpVend.ExchAdjTaxable_RU == ExchAdjTaxableType_W::No)
    {
        tmpVend.TaxLedgerDimension_RU   = 0;
        tmpVend.TaxAccountName_RU       = '';
        tmpVend_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(false);
        tmpVend_ds.rereadReferenceDataSources();
    }
    else
    {
        tmpVend_ds.object(fieldNum(TmpCurrencyLedgerGainLossAccount, TaxLedgerDimension_RU)).allowEdit(true);
    }
}

