[Form]
public class LedgerJournalControl extends FormRun
{
    LedgerJournalControlForm    formHelper;
}

public void close()
{
    // commit any changes that were made for the previously selected journal control
    // and cleanup unused resources
    element.commitDetailChanges();

    super();
}

/// <summary>
/// Commit any changes that have been made in the details grid.
/// </summary>
public void commitDetailChanges()
{
    if (formHelper != null)
    {
        // commit any changes that were made for the previously selected journal control
        formHelper.commitCurrentChanges();

        // clean up associated resources prior to abandoning the instance
        formHelper.cleanupResources();
        formHelper = null;
    }
}

public void init()
{
    super();

    tmpLedgerJournalControlDetail_Q.dataSourceName('tmpDetailLegalEntity').fields().addField(fieldNum(CompanyInfo, DataArea));
}

private void initForJournalControlDetail()
{
    // since we are programatically setting a field that is associated with a reference data source (i.e. LedgerLegalEntity)
    // we need to force the reference data source to be reread.
    tmpLedgerJournalControlDetail_DS.rereadReferenceDataSources();

    // clear values on the row that are invalidated by a change to the selected company
    // note the refrence group fields, both the datasource field and the control field had to be cleared
    tmpLedgerJournalControlDetail.DimensionHierarchy = 0;
    dimensionHierarchy.value(0);
    tmpLedgerJournalControlDetail.DimensionAttribute = 0;
    tmpLedgerJournalControlDetail.RangeFrom = '';
    tmpLedgerJournalControlDetail.RangeTo = '';

    dimensionHierarchy.allowEdit(tmpLedgerJournalControlDetail.LedgerLegalEntity != 0);
    dimensionAttribute.allowEdit(false);
    rangeFrom.allowEdit(false);
    rangeTo.allowEdit(false);
}

public void linkActive()
{
    super();

    // position the cursor to the passed in record, if supplied
    if (element.args() && element.args().record())
    {
        element.args().lookupRecord(element.args().record());
        this.research(false);
    }
}

public void init()
{
    QueryBuildDataSource    qbdsJournalName;
    str                     notJournalTypesRangeValue;

    super();

    // limit the journal names displayed to those LedgerJournalTypes for which journal control can be applied
    qbdsJournalName = ledgerJournalName_DS.query().dataSourceTable(tableNum(LedgerJournalName));
    notJournalTypesRangeValue = strFmt('((JournalType != %1) && (JournalType != %2) && (JournalType != %3) && (JournalType != %4))',
                                       enum2int(LedgerJournalType::Allocation),
                                       enum2int(LedgerJournalType::Elimination),
                                       enum2int(LedgerJournalType::BankChequeReversal),
                                       enum2int(LedgerJournalType::BankDepositPaymCancel));
    qbdsJournalName.addRange(fieldNum(LedgerJournalName,JournalType)).value(notJournalTypesRangeValue);
    qbdsJournalName.addSortField(fieldNum(LedgerJournalName, JournalName));

    // we will display all elgible journal names regardless of how the form was opened
    qbdsJournalName.clearDynalinks();
}

public int active()
{
    int returnValue;

    returnValue = super();

    // commit any changes that were made for the previously selected journal control
    // and cleanup unused resources
    element.commitDetailChanges();

    tmpLedgerJournalControlDetail_DS.research();
    DetailPage.enabled(false);
    tmpDetailAdd.enabled(false);
    tmpDetailRemove.enabled(false);

    return returnValue;
}

public void delete()
{
    super();

    if (formHelper != null)
    {
        formHelper.cleanupResources();
        formHelper = null;
    }

    this.initDetailGridForCurrentJournalControl();

    tmpLedgerJournalControlDetail_DS.research();
}

private void initDetailGridForCurrentJournalControl()
{
    // configure the details tab for the current journal control record
    if (ledgerJournalControl.AccountType == LedgerJournalACTypeAll::All ||
        ledgerJournalControl.AccountType == LedgerJournalACTypeAll::Ledger)
    {
        // details tab is only active/populated when the selected AccountType includes Ledger
        DetailPage.enabled(true);
        tmpDetailAdd.enabled(true);
        tmpDetailRemove.enabled(true);

        // spin up a new form helper for the currently selected header record
        // this will also produce the needed temp records to be displayed in the details tab
        if (ledgerJournalControl.RecId != 0)
        {
            formHelper = LedgerJournalControlForm::construct(ledgerJournalControl.RecId, tmpLedgerJournalControlDetail_DS.cursor());
        }
    }
    else
    {
        // disable the details tab as details are not allowed when the AccountType does not include Ledger
        DetailPage.enabled(false);
        tmpDetailAdd.enabled(false);
        tmpDetailRemove.enabled(false);
    }
}

public int active()
{
    int                             returnValue;

    // commit any changes that were made for the previously selected journal control
    // and cleanup unused resources
    element.commitDetailChanges();

    boolean isExisting = (ledgerJournalControl.RecId == 0);
    ledgerJournalControl_DS.allowEdit(isExisting);

    this.initDetailGridForCurrentJournalControl();

    returnValue = super();

    // display the temp records, if any
    tmpLedgerJournalControlDetail_DS.research();

    return returnValue;
}

public edit Name editDimensionAttributeName(boolean _set, TmpLedgerJournalControlDetail _detail, Name _name)
{
    if (_set)
    {
        _detail.DimensionAttribute = DimensionAttribute::findByLocalizedName(_name).RecId;
    }

    return DimensionAttribute::find(_detail.DimensionAttribute).localizedName();
}

public boolean validateWrite()
{
    boolean isValid;

    isValid = super();

    if (isValid)
    {
        if (tmpLedgerJournalControlDetail.RangeFrom == '' && tmpLedgerJournalControlDetail.RangeTo == '')
        {
            isValid = checkFailed("@SYS311178");
            rangeFrom.setFocus();
        }
        else
        {
            // client validation succeeded, continue with business rule validation
            isValid = formHelper.processTempDetail(tmpLedgerJournalControlDetail_DS.cursor());

            if (! isValid)
            {
                // validation failed
                checkFailed("@SYS310074");
            }
        }
    }

    return isValid;
}

public int active()
{
    int     ret;

    if (tmpLedgerJournalControlDetail.RecId != 0)
    {
        // only the range values can be edited on existing detail lines
        ledgerCompanyId.allowEdit(false);
        dimensionHierarchy.allowEdit(false);
        dimensionAttribute.allowEdit(false);
        rangeFrom.allowEdit(true);
        rangeTo.allowEdit(true);
    }

    ret = super();

    return ret;
}

public void initValue()
{
    // pre-set the company accounts column in the details grid
    if (ledgerJournalControl.JournalLegalEntity == 0)
    {
        // company selection on header grid is <All> (LedgerLegalEntity == 0), allow editing of company and default to current company
        tmpLedgerJournalControlDetail.LedgerLegalEntity = CompanyInfo::current();
        ledgerCompanyId.allowEdit(true);
        ledgerCompanyId.setFocus();

        element.initForJournalControlDetail();
    }
    else
    {
        // a specific company was selected on the header grid, all detail lines must use that same company
        tmpLedgerJournalControlDetail.LedgerLegalEntity = ledgerJournalControl.JournalLegalEntity;
        ledgerCompanyId.allowEdit(false);
        dimensionHierarchy.setFocus();
    }

    dimensionHierarchy.allowEdit(true);

    // lock other fields that cannot be set until other values have been set
    dimensionAttribute.allowEdit(false);
    rangeFrom.allowEdit(false);
    rangeTo.allowEdit(false);

    super();
}

public void delete()
{
    TmpLedgerJournalControlDetail   tmpJournalControlDetail;
    boolean                         mustUpdateConstraintTree;

    mustUpdateConstraintTree = (tmpLedgerJournalControlDetail.RecId != 0);

    // keep a snaphot of the current temp record for use after the record has been deleted by super()
    tmpJournalControlDetail = tmpLedgerJournalControlDetail_DS.cursor().data();

    ttsbegin;

    // invoke super to proceed with deletion of the TmpLedgerJournalControlDetail record prior to updating the constraint tree
    super();

    if (mustUpdateConstraintTree)
    {
        formHelper.processDeletedTempDetail(tmpJournalControlDetail);
    }

    ttscommit;
}

public void lookupDimensionAttributeValue(FormControl _formControl, str _filterStr)
{
    SysTableLookup                  sysTableLookup;
    Query                           lookupQuery;
    QueryBuildDataSource            lookupDataSource;
    DimensionAttributeViewContract  dimensionAttributeViewContract;
    TableId                         translationViewId;

    // Company and Segment must have been previously selected prior to selecting the range value
    Debug::assert(tmpDetailLegalEntity.RecId != 0);
    Debug::assert(tmpDetailAttribute.RecId != 0);

    dimensionAttributeViewContract = DimensionAttribute::getViewContract(tmpDetailAttribute.RecId);

    // Provide a custom lookup form that displays the ValueAttribute and NameAttribute fields of the translation view.
    translationViewId = dimensionAttributeViewContract.parmViewId();
    sysTableLookup = SysTableLookup::newParameters(translationViewId, _formControl, true);

    sysTableLookup.addLookupfield(dimensionAttributeViewContract.parmValueFieldId());
    sysTableLookup.addLookupfield(dimensionAttributeViewContract.parmNameFieldId());

    // Create a custom query to filter on input values and context from the form.
    lookupQuery = DimensionAttribute::getBackingEntityTranslationViewQuery(tmpDetailAttribute.RecId);
    lookupDataSource = lookupQuery.dataSourceTable(translationViewId);
    lookupQuery.allowCrossCompany(true);

    // Allow the LedgerDimensionController static method to determine whether to update the query for categorized values.
    DimensionAttribute::restrictQueryToCategorizedValues(lookupDataSource, tmpDetailAttribute.RecId, Ledger::chartOfAccounts(tmpLedgerJournalControlDetail.LedgerLegalEntity), true);

    // Hook the query up to the lookup form.
    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    sysTableLookup.performFormLookup();
}

public void modified()
{
    // clear values on the row that are invalidated by a change to the selected hierarchy
    // note the refrence group fields, both the datasource field and the control field had to be cleared
    tmpLedgerJournalControlDetail.DimensionAttribute = 0;
    tmpLedgerJournalControlDetail.RangeFrom = '';
    tmpLedgerJournalControlDetail.RangeTo = '';

    dimensionAttribute.allowEdit(tmpLedgerJournalControlDetail.DimensionHierarchy != 0);
    rangeFrom.allowEdit(false);
    rangeTo.allowEdit(false);

    super();
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    DimensionHierarchy  resolvedRecord;
    Name                hierarchyName;
    container           hierarchyDataAreas;

    // Do not call super as we're providing our own disambiguation logic.

    // get the company associated LegalEntity of the row
    hierarchyDataAreas += tmpDetailLegalEntity.DataArea;

    // get the hierarchy name value that was input by the user
    hierarchyName = _formReferenceControl.filterValue(AbsoluteFieldBinding::construct(fieldStr(DimensionHierarchy, Name), tableStr(DimensionHierarchy))).valueForQueryRange();

    // retrieve the record associated with the input values and the fixed context from the form
    select firstonly crosscompany:hierarchyDataAreas
        resolvedRecord
    where
        resolvedRecord.Name == hierarchyName &&
        resolvedRecord.StructureType == DimensionHierarchyType::AccountStructure &&
        resolvedRecord.IsDraft == NoYes::No &&
        resolvedRecord.IsSystemGenerated == NoYes::No;

    return resolvedRecord;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    DimensionHierarchy      selectedRecord;
    dataAreaId              hierarchyDataArea;
    SysReferenceTableLookup sysTableLookup;
    Query                   lookupQuery;
    QueryBuildDataSource    lookupDataSource;

    // Do not call super as we're providing our own custom lookup logic

    // get the company associated LegalEntity of the row
    hierarchyDataArea = tmpDetailLegalEntity.DataArea;

    // Provide a custom lookup form that displays the Name and Description fields
    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(DimensionHierarchy), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(DimensionHierarchy, Name));
    sysTableLookup.addLookupfield(fieldNum(DimensionHierarchy, Description));

    // Create a custom Query to filter on input values and context from the form
    lookupQuery = new Query();
    lookupQuery.allowCrossCompany(true);

    // Only show user-created, active account structures
    lookupDataSource = lookupQuery.addDataSource(tableNum(DimensionHierarchy));
    lookupDataSource.addRange(fieldNum(DimensionHierarchy, StructureType)).value(queryValue(DimensionHierarchyType::AccountStructure));
    lookupDataSource.addRange(fieldNum(DimensionHierarchy, IsDraft)).value(queryValue(NoYes::No));
    lookupDataSource.addRange(fieldNum(DimensionHierarchy, IsSystemGenerated)).value(queryValue(NoYes::No));

    // and only the structures that exist in the ledger for the specified legal entity
    lookupDataSource = lookupDataSource.addDataSource(tableNum(LedgerStructure));
    lookupDataSource.addLink(fieldNum(DimensionHierarchy, RecId), fieldNum(LedgerStructure, DimensionHierarchy));
    lookupDataSource.joinMode(JoinMode::InnerJoin);
    lookupDataSource.addRange(fieldNum(LedgerStructure, Ledger)).value(
        queryValue(Ledger::primaryLedger(tmpLedgerJournalControlDetail.LedgerLegalEntity)));

    // hook the query up to the lookup form
    sysTableLookup.parmQuery(lookupQuery);

    // Return the record selected by the user.
    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}

