[Form]
public class LedgerJournalFurtherPosting extends FormRun
{
    LedgerShowDebCRedController_CN showDRsCRsController_CN;
    boolean                        enableShowDRsCRs_CN;

}

/// <summary>
/// Show the LedgerPostingJournalId field in form <c>LedgerJournalFurtherPosting</c>
/// if the Legal Entity is in the BE Country Region.
/// </summary>
private void showLedgerPostingJournalId_BE()
{
    #ISOCountryRegionCodes

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBE]))
    {
        generalJournalEntry_LedgerPostingJournalId.visible(true);
    }
}

public void init()
{
    enableShowDRsCRs_CN = LedgerShowDebCRedController_CN::isShowDRsCRsFeatureEnabled();

    if (enableShowDRsCRs_CN)
    {
        showDRsCRsController_CN = LedgerShowDebCRedController_CN::construct(GeneralJournalAccountEntry.TableId);
    }

    super();

    this.showLedgerPostingJournalId_BE();

    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        VendTrans_AccountNum.visible(true);
        VendTable_Name.visible(true);
        LedgerJournalTrans_RemittanceLocation.visible(true);
        LedgerJournalTrans_RemittanceAddress.visible(true);
    }
}

public void displayOption(Common _record, FormRowDisplayOption _options)
{
    super(_record, _options);

    if (LedgerShowDebCRedController_CN::isNegativeAmountsInRedFeatureEnabled())
    {
        showDRsCRsController_CN.showNegativeAmountsInRed(_record, _options);
    }
}

        public void init()
        {
            super();

            if (enableShowDRsCRs_CN)
            {
                showDRsCRsController_CN.addAmountControl(ledgerTrans_TransactionAmount);
                showDRsCRsController_CN.addAmountControl(ledgerTrans_AccountingAmount);
                showDRsCRsController_CN.addAmountCreditControl(generalJournalLine_AmountCurCredit);
                showDRsCRsController_CN.addAmountCreditControl(generalJournalLine_AmountMstCredit);
                showDRsCRsController_CN.addAmountDebitControl(generalJournalLine_AmountCurDebit);
                showDRsCRsController_CN.addAmountDebitControl(generalJournalLine_AmountMstDebit);

                showDRsCRsController_CN.initControls();
            }

			// Exclude interunit transactions from the form
            Query q = generalJournalAccountEntry_ds.query();
            QueryBuildDataSource ds = q.dataSourceTable(tablenum(GeneralJournalAccountEntry));

            QueryBuildRange range = ds.addRange(fieldnum(GeneralJournalAccountEntry, PostingType));
            range.value(strFmt('((%1 != %2) && (%1 != %3))', fieldStr(GeneralJournalAccountEntry, PostingType),
                                enum2int(LedgerPostingType::InterunitCredit), enum2int(LedgerPostingType::InterunitDebit)));
        }

public void executeQuery()
{
    QueryBuildDataSource ds = this.query().dataSourceTable(tablenum(GeneralJournalEntry));

    GeneralJournalEntry::addSecureRangeForLedger(ds);

    super();
}

public void init()
{
    Query q;
    QueryBuildDataSource ds;
    QueryBuildRange range;

    super();

    q = generalJournalAccountEntry_ds.query();
    ds = q.dataSourceTable(tablenum(LedgerEntry));

    ds.clearDynalinks();

    range = ds.addRange(fieldnum(LedgerEntry, IsBridgingPosting));
    range.value(SysQuery::value(NoYes::Yes));
}

[Hookable(false)]
public void init()
{
    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        this.linkType(FormLinkType::InnerJoin);
    }

    super();
}

[Hookable(false)]
public void executeQuery()
{
    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        QueryBuildDataSource qbdsTrans = LedgerJournalTrans_ds.queryBuildDataSource();
        qbdsTrans.addRange(fieldNum(LedgerJournalTrans, Invisible)).value(SysQuery::value(NoYes::No));
        qbdsTrans.addRange(fieldNum(LedgerJournalTrans, AccountType)).value(SysQuery::value(LedgerJournalACType::Vend));
    }

    super();
}

[Hookable(false)]
public void init()
{
    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        this.linkType(FormLinkType::InnerJoin);
    }

    super();

    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        QueryBuildDataSource qbdsTrans = LedgerJournalTrans_ds.queryBuildDataSource();
        qbdsTrans.clearLinks();
        qbdsTrans.addLink(fieldNum(SubledgerVoucherGeneralJournalEntry, Voucher), fieldNum(LedgerJournalTrans, Voucher));
        qbdsTrans.addLink(fieldNum(SubledgerVoucherGeneralJournalEntry, AccountingDate), fieldNum(LedgerJournalTrans, TransDate));
    }
}

[Hookable(false)]
public void init()
{
    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        this.linkType(FormLinkType::OuterJoin);
    }

    super();

    if (FeatureStateProvider::isFeatureEnabled(BankBridgingVendorTransactionDetailsFeature::instance()))
    {
        QueryBuildDataSource qbdsVendTrans = VendTrans_ds.queryBuildDataSource();
        qbdsVendTrans.clearLinks();
        qbdsVendTrans.addLink(fieldNum(LedgerJournalTrans, VendTransId), fieldNum(VendTrans, RecId));
    }
}

public void clicked()
{
    GeneralJournalAccountEntry localGeneralJournalAccountEntry;
    GeneralJournalAccountEntry loopGeneralJournalAccountEntry;
    LedgerEntry localLedgerEntry;
    LedgerEntryJournal localLedgerEntryJournal;
    GeneralJournalEntry localGeneralJournalEntry;
    FormDataSource fds;
    LedgerJournalTrans ledgerJournalTransLocal;

    fds = generalJournalAccountEntry_ds;

    ttsbegin;

    if (element.args().record().TableId == tablenum(LedgerJournalTrans))
    {
        ledgerJournalTransLocal = element.args().record() as LedgerJournalTrans;

        // only delete for this table
        if (!ledgerJournalTransLocal.RecId)
        {
            // delete the journal AccountEntry focus is on to enable reuse of the voucher if possible
            ledgerJournalTransLocal.delete();
        }
    }

    // Initiate process
    for (loopGeneralJournalAccountEntry = getFirstSelection(fds);
        loopGeneralJournalAccountEntry;
        loopGeneralJournalAccountEntry = fds.getNext())
    {
        [localGeneralJournalAccountEntry, localLedgerEntryJournal, localGeneralJournalEntry, localLedgerEntry] =
            GeneralLedgerExtension::findRelated(loopGeneralJournalAccountEntry.RecId);

        LedgerJournalTable ledgerJournalTable = element.ledgerJournalTable();

        if (FeatureStateProvider::isFeatureEnabled(BankBridgedTransAutoClearingFeature::instance()) && ledgerJournalTable.JournalType == LedgerJournalType::Daily)
        {
            LedgerJournalTrans ledgerJournalTransBridged;
            SubledgerVoucherGeneralJournalEntry subledgerVoucherGeneralJournalEntryLocal;

            select firstonly ledgerJournalTransBridged
                where ledgerJournalTransBridged.JournalNum == localLedgerEntryJournal.JournalNumber
                    && ledgerJournalTransBridged.OffsetLedgerDimension == localGeneralJournalAccountEntry.LedgerDimension
                    join RecId from subledgerVoucherGeneralJournalEntryLocal
                        where subledgerVoucherGeneralJournalEntryLocal.Voucher == ledgerJournalTransBridged.Voucher
                            && subledgerVoucherGeneralJournalEntryLocal.VoucherDataAreaId == ledgerJournalTransBridged.DataAreaId
                            && subledgerVoucherGeneralJournalEntryLocal.GeneralJournalEntry == localGeneralJournalEntry.RecId;

            BankBridgedTransClearingLock bankBridgedTransClearingLock = new BankBridgedTransClearingLock(ledgerJournalTransBridged.RecId);

            try
            {
                bankBridgedTransClearingLock.enterBridgeClearingLock();

                if (bankBridgedTransClearingLock.lockAcquired)
                {
                    LedgerTransFurtherPosting::createAndGetLedgerJournalTransFromGenJour(
                        localGeneralJournalAccountEntry,
                        localLedgerEntryJournal,
                        localGeneralJournalEntry,
                        localLedgerEntry,
                        ledgerJournalTable);
                }
            }
            finally
            {
                bankBridgedTransClearingLock.exitBridgeClearingLock();
            }
        }
        else
        {
            LedgerTransFurtherPosting::createAndGetLedgerJournalTransFromGenJour(
                localGeneralJournalAccountEntry,
                localLedgerEntryJournal,
                localGeneralJournalEntry,
                localLedgerEntry,
                ledgerJournalTable);
        }
    }

    ttscommit;

    element.args().caller().research();
    element.close();
}
