/// <summary>
/// Handles the event that fires when focus is given to the form.
/// </summary>
/// <param name="_active">
/// true if active; otherwise, false.
/// </param>
public void activate(boolean _active)
{
    super(_active);

    journalFormTrans.manageEditingModeForApprovedWFApproval();
}

void bookRefresh(boolean _isClosing = false)
{
    LedgerIJournalFormRefreshable callerForm = element.args().caller();

    if (callerForm)
    {
        callerForm.refresh();
    }

    if (!_isClosing)
    {
        ledgerJournalTrans_ds.reread();
    }
}

public void callerRefresh()
{
    ledgerJournalEngine.clearCorrectedTaxAmount(ledgerJournalTrans);
    ledgerJournalTrans_ds.refresh();
}

/// <summary>
/// Sets the editable property of various datasource fields for the form.
/// </summary>
public void enableFieldsActive()
{
    if (isIsoDEAT)
    {
        specialAddition.visible(LedgerJournalTrans_Asset.TransType == AssetTransTypeJournal::Acquisition
        || LedgerJournalTrans_Asset.TransType == AssetTransTypeJournal::AcquisitionAdj);
    }       
}

/// <summary>
/// Sets the editable property of reporting currency fields on the datasource <c>LedgerJournalTrans_Asset</c>.
/// </summary>
public void enableFieldsActiveReportingCurrency()
{       
    boolean isDepreciation = ledgerJournalTrans_Asset.isDepreciationTransactionType();
    boolean enableDebitCreditReportingFields;

    if (isDepreciation)
    {
        enableDebitCreditReportingFields = Ledger::reportingCurrency() != '' && ledgerJournalTrans.CurrencyCode != Ledger::reportingCurrency() ? true : false;
    }

    if (LedgerJournalTransAssetReportingCurrencyEnablementFlight::instance().isEnabled())
    {
        ledgerJournalTrans_Asset_ds.object(fieldNum(LedgerJournalTrans_Asset, AmountCreditReportingCurrency)).allowEdit(enableDebitCreditReportingFields);
        ledgerJournalTrans_Asset_ds.object(fieldNum(LedgerJournalTrans_Asset, AmountDebitReportingCurrency)).allowEdit(enableDebitCreditReportingFields);
    }
    else
    {
        ledgerJournalTrans_Asset_ds.object(fieldNum(LedgerJournalTrans_Asset, AmountCreditReportingCurrency)).allowEdit(enableDebitCreditReportingFields && LedgerJournalTrans.AmountCurCredit != 0);
        ledgerJournalTrans_Asset_ds.object(fieldNum(LedgerJournalTrans_Asset, AmountDebitReportingCurrency)).allowEdit(enableDebitCreditReportingFields && LedgerJournalTrans.AmountCurDebit != 0);
    }

    AssetBookTableDerivedJournal_ds.object(fieldNum(AssetBookTableDerivedJournal, ReportingCurrencyAmount)).allowEdit(isDepreciation);
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, CurrencyCode)).allowEdit(!isDepreciation);
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Company)).allowEdit(!isDepreciation);
    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, OffsetCompany)).allowEdit(!isDepreciation);
}

