    ledgerJournalTransZakat_SA_ds.object(fieldNum(LedgerJournalTransZakat_SA, SalesId)).allowEdit(ledgerJournalTransZakat_SA.SalesType == SalesTypeZakat_SA::Sales);
    //</GSA>
}

