        isIsoRU = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]);

        isIsoPL = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL]);
        isIsoDEAT = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoDE, #isoAT]);

        super();

        // <GSA>
        zakatParameterEnabled = LedgerParameters::find().IsZakatEnabled_SA;
        // </GSA>

        LedgerJournalTrans_AccountNum.parmPostingType(LedgerPostingType::LedgerJournal);
        LedgerJournalTrans_AccountNum1.parmPostingType(LedgerPostingType::LedgerJournal);
        LedgerJournalTrans_OffsetAccount.parmPostingType(LedgerPostingType::LedgerJournal);
