/// <summary>
/// Set the amount credit to disposal amount for Poland disposal scrap.
/// </summary>
void modifyDisposalAmount_PL()
{
    AmountMST   amountCredit;

    if (isIsoPL)
    {
        this.updateSaleFactorField();

        if ((ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalSale
            || ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalScrap)
            && ledgerJournalTrans_Asset.BookId
            && ledgerJournalTrans.parmAccount())
        {
            ledgerJournalTrans.AmountCurDebit  = 0;
            amountCredit = AssetPostDisposal_Pl::getDisposalAmountBySaleFactor(
                    ledgerJournalTrans.parmAccount(),
                    ledgerJournalTrans_Asset.BookId,
                    ledgerJournalTrans.TransDate,
                    ledgerJournalTrans.CurrencyCode,
                    ledgerJournalTrans_Asset.SaleFactor_PL);

            ledgerJournalTrans.AmountCurCredit = CurrencyExchangeHelper::amount(amountCredit, LedgerJournalTable.CurrencyCode);
        }
    }
}

void setDebCredProposal(boolean _init)
{
    if (_init)
    {
        debCredProposal = DebCredProposal::None;
    }

    if (debCredProposal != debCredProposalOld)
    {
        switch (debCredProposal)
        {
            case DebCredProposal::Credit:
                ledgerJournalTrans_AmountCurDebit.skip(true);
                amountCurDebit.skip(true);
                ledgerJournalTrans_AmountCurCredit.skip(false);
                amountCurCredit.skip(false);
                break;

            case DebCredProposal::Debit:
                ledgerJournalTrans_AmountCurCredit.skip(true);
                amountCurCredit.skip(true);
                ledgerJournalTrans_AmountCurDebit.skip(false);
                amountCurDebit.skip(false);
                break;

            case DebCredProposal::None:
                ledgerJournalTrans_AmountCurCredit.skip(false);
                amountCurCredit.skip(false);
                ledgerJournalTrans_AmountCurDebit.skip(false);
                amountCurDebit.skip(false);
                break;
        }
        debCredProposalOld = debCredProposal;
    }
}

public void setDisassemblingAmount_JP()
{
    ledgerJournalTrans.AmountCurCredit = CurrencyExchangeHelper::curAmount(AssetComponent_JP::calcCurrentDisasssmeblingAmount(LedgerJournalTrans_Asset.AssetId), ledgerJournalTrans.CurrencyCode);
    ledgerJournalTrans.AmountCurDebit = 0;
}

void setFunctionButton()
{
    buttonFunctions.enabled(true);
}

/// <summary>
/// Set the partial scrap percent when credit amount is changed for Poland disposal scrap.
/// </summary>
void updateSaleFactor_PL()
{
    AmountMST netBookValue;

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL])
        && ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalScrap
        && ledgerJournalTrans_Asset.BookId
        && ledgerJournalTrans.parmAccount())
    {
        if (ledgerJournalTrans.AmountCurCredit)
        {
            netBookValue = AssetSumCalc_Trans::newAssetYear(ledgerJournalTrans.parmAccount(),
                                                        ledgerJournalTrans_Asset.BookId,
                                                        ledgerJournalTrans.TransDate).netBookValue();

            if (netBookValue)
            {
                ledgerJournalTrans_Asset.SaleFactor_PL = ledgerJournalTrans.AmountCurCredit / netBookValue * 100;
            }
        }
        else
        {
            ledgerJournalTrans_Asset.SaleFactor_PL = 100;
        }
    }
    // </GEEPL>
}

void updateSaleFactorField()
{
    if (ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalSale ||
        ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalScrap)
    {
        assetSaleFactor_PL.allowEdit(true);
    }
    else
    {
        assetSaleFactor_PL.allowEdit(false);
    }
}

void visibleAccountNum_IN()
{
    if (ledgerJournalTable.CurrentOperationsTax == CurrentOperationsTax::Tax || ledgerJournalTable.CurrentOperationsTax == CurrentOperationsTax::None)
    {
        ButtonCreateBonus.visible(true);
        ledgerJournalTrans_Asset_ds.object(fieldNum(LedgerJournalTrans_Asset, AssetGroup_IN)).visible(true);
        ledgerJournalTrans_Asset.AssetGroup_IN = AssetTable::find(ledgerJournalTrans.parmAccount()).AssetGroup;
    }
    else
    {
        ButtonCreateBonus.visible(false);
        ledgerJournalTrans_Asset_ds.object(fieldNum(LedgerJournalTrans_Asset, AssetGroup_IN)).visible(false);
    }

    if (AssetBook::find(ledgerJournalTrans.parmAccount(), ledgerJournalTrans_Asset.BookId).AssetGroupDepreciation_IN   &&
       (ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::Depreciation                                   ||
        ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DepreciationAdj))
    {
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, LedgerDimension)).enabled(false);
    }
    else
    {
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, LedgerDimension)).enabled(true);
    }
    // </GIN>
}

void visibleNotificationCentralBank()
{
    #ISOCountryRegionCodes

    if (ledgerJournalTrans.AccountType == LedgerJournalACType::Cust ||
        ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Cust)
    {
        groupNotificationToTheCentralBank.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]));
    }
    else
    {
        if (ledgerJournalTrans.AccountType == LedgerJournalACType::Vend ||
            ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Vend)
        {
            groupNotificationToTheCentralBank.visible(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO]));
        }
        else
        {
            groupNotificationToTheCentralBank.visible(false);
        }
    }
}

/// <summary>
/// Set the default transaction text.
/// </summary>
public LedgerJournalTransTxt setDefaultTransactionText()
{
    TransactionTxt  transactionTxt;
    TransactionTextContext  transactionTextContext;
    LedgerJournalTransTxt ledgerJournalTransTxt;

    AssetTable assetTable = AssetTable::findByLedgerDimension(ledgerJournalTrans.LedgerDimension);

    transactionTxt = TransactionTxt::construct(LedgerTransTxt::FixedAssetsPostingLedger);
    transactionTxt.setDate(ledgerJournalTrans.TransDate);
    transactionTxt.setVoucher(ledgerJournalTrans.Voucher);
    transactionTxt.setLanguage(currentUserLanguage());
    transactionTxt.setFormLetter(assetTable.AssetId);
    transactionTxt.setKey1(assetTable.AssetId);
    transactionTxt.setKey2(assetTable.AssetGroup);

    if (TransactionTextContext::isTypeSupported(LedgerTransTxt::FixedAssetsPostingLedger))
    {
        transactionTextContext = TransactionTextContext::newForTransactionType(LedgerTransTxt::FixedAssetsPostingLedger);
        transactionTextContext.setTableBuffer(assetTable);
        transactionTxt.setTransactionTextContext(transactionTextContext);
    }

    ledgerJournalTransTxt = transactionTxt.txt();

    return ledgerJournalTransTxt;
}

