public boolean validate()
{
    AmountMST   amountCredit;
    boolean     ret;

    ret = super();

    if (ret
        && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoPL])
        && ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalScrap)
    {
        amountCredit = AssetSumCalc_Trans::newAssetYear(ledgerJournalTrans.parmAccount(),
                                                        ledgerJournalTrans_Asset.BookId,
                                                        ledgerJournalTrans.TransDate).netBookValue();

        if (ledgerJournalTrans.AmountCurCredit > CurrencyExchangeHelper::amount(amountCredit, ledgerJournalTable.CurrencyCode))
        {
            ret = checkFailed("@GLS110660");
        }
    }

    return ret;
    // </GEEPL>
}

public void modified()
{                
    if (isIsoRU)
    {
        ledgerJournalEngine.amountCurCreditModified(ledgerJournalTrans);
        ledgerJournalEngine.clearTaxAmountCache(ledgerJournalTrans);
        ledgerJournalEngine.cashDiscCodeModified(ledgerJournalTrans);
        ledgerJournalTrans_ds.refresh();
    }
    else
    {
        super();

        LedgerJournalEngine::updateAssetBookTableDerivedJournals(ledgerJournalTrans);
        assetBookTableDerivedJournal_ds.research();

        // <GEEPL>
        element.updateSaleFactor_PL();
        // </GEEPL>
        element.setReportingCurrencyAmountsForDepreciation();
    }           
}

