/// <summary>
/// Standard event handler that is called each time a record is read by the data source.
/// </summary>
/// <returns>
/// An integer return code.
/// </returns>
/// <remarks>
/// An extra verification was added for French derogatory depreciation to prevent the user
/// editing and deleting derived value model used for derogatory depreciation.
/// </remarks>

public int active()
{
    int ret;

    ret = super();
    // For derogatory depreciation value model the derived transactions needs to be read only.
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR])
        && AssetBookTable::find(assetBookTableDerivedJournal.AssetBookId).IsDerogatoryBook_FR)
    {
        this.allowEdit(false);
        this.allowDelete(false);
    }
    else
    {
        this.allowEdit(true);
        this.allowDelete(true);
    }

    return ret;
}

public void modified()
{
    super();

    assetBookTableDerivedJournal.checkSign(ledgerJournalTrans);
}

/// <summary>
/// Validates the AssetBookId.
/// </summary>
/// <returns>
/// true:entered value is valid; false:entered value is invalid
/// </returns>
/// <remarks>
/// Error messages are given from this method.
/// </remarks>
public boolean validate()
{
    boolean     isValid = true;

    if (!ledgerJournalTrans.Company)
    {
        ledgerJournalTrans.Company = curext();
    }
    else if (!xDataArea::exist(ledgerJournalTrans.Company))
    {
        isValid = checkFailed(strFmt("@SYS10666", ledgerJournalTrans.Company));
    }

    changecompany(ledgerJournalTrans.Company)
    {
        if (!AssetBook::exist(ledgerJournalTrans.parmAccount(), assetBookTableDerivedJournal.AssetBookId))
        {
            isValid = checkFailed(strFmt("@FixedAssets:FA_MessageError_BookDoesNotExistForFixedAsset", assetBookTableDerivedJournal.AssetBookId, ledgerJournalTrans.parmAccount()));
        }
    }

    if (isValid)
    {
        isValid = super();
    }

    return isValid;
}

/// <summary>
/// Provides non-standard lookup for the derived Value model (AssetBookId) field.
/// </summary>
/// <param name="_formControl">
/// Identifies calling form.
/// </param>
/// <param name="_filterStr">
/// Asset Id to restrict on.
/// </param>
/// <remarks>
/// Will show 'open' (AssetStatus of Open, Closed, NoAcquisition) asset books for all posting layers that are
/// defined for the specified AssetId
/// </remarks>
public void lookup(FormControl _formControl, str _filterStr)
{
    AssetBook::lookupOpenBookIdForAsset(_formControl,
                                        ledgerJournalTrans.parmAccount(),
                                        false,  // ignore CurrentOperationsTax so all posting layers are shown
                                        ledgerJournalTable.CurrentOperationsTax,
                                        ledgerJournalTrans.Company);
}

