}

private boolean isDerogatoryDerivedTransaction_FR(AssetTransTypeJournal _assetJournalType)
{
    boolean returnValue = false;

    if (_assetJournalType == AssetTransTypeJournal::Acquisition
        || _assetJournalType == AssetTransTypeJournal::AcquisitionAdj
        || _assetJournalType == AssetTransTypeJournal::DisposalSale
        || _assetJournalType == AssetTransTypeJournal::DisposalScrap
        || _assetJournalType == AssetTransTypeJournal::WriteDownAdj
        || _assetJournalType == AssetTransTypeJournal::WriteUpAdj)
    {
        returnValue = true;
    }

    return returnValue;
}

public void initValue()
{
    super();

    ledgerJournalEngine.initValueAsset(ledgerJournalTrans_Asset);
    element.enableDepreciationStartDate();
}

public void write()
{
    AssetBookTableDerivedJournal localAssetBookTableDerivedJournal;

    if (ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityWriteEvent())
    {
        // Only perform the write if called from the LedgerJournalTrans datasource's write event.

        if (!ledgerJournalTrans_Asset.RecId
            ||(SysCountryRegionCode::isLegalEntityInCountryRegion([#isoFR])
                && this.isDerogatoryDerivedTransaction_FR(ledgerJournalTrans_Asset.TransType)
                && AssetBookTable::find(ledgerJournalTrans_Asset.BookId).AssetDerogatoryModel_FR)
            || ledgerJournalTrans_Asset.AssetId != ledgerJournalTrans_Asset.orig().AssetId
            || ledgerJournalTrans_Asset.BookId != ledgerJournalTrans_Asset.orig().BookId)
        {
            // Only create these records once as once the user makes changes we should not override them.
            // This should work okay as moving to the derived tabs will force a save on the journal records.
            LedgerJournalEngine::createDefsForAssetBookTableDerivedJourn(ledgerJournalTrans, ledgerJournalTrans_Asset);

            assetBookTableDerivedJournal_ds.research();
        }

        super();
    }
}

public int active()
{
    int ret;

    ret = super();

    element.enableDepreciationStartDate();

    // <GJP>
    if (isISOJP)
    {
        if (LedgerJournalTrans_Asset.TransType == AssetTransTypeJournal::ExtraordinaryDepreciation
            && LedgerJournalTrans_Asset.DocumentType_JP == AssetDocumentType_JP::AdditionalDepReserveAllocation)
        {
            AssetSpecialDepDistributionView_JP.visible(true);
        }
        else
        {
            AssetSpecialDepDistributionView_JP.visible(false);
        }

        if (AssetDepDistribution_JP::hasDistribution(tableNum(LedgerJournalTrans_Asset), LedgerJournalTrans_Asset.RecId) 
            || (ledgerJournalTable.isInWFApprovalProcess() 
                && !ledgerJournalTable.isWFUserPermittedToEdit(journalFormTrans.parmWorkflowWorkItemTable())))
        {
            this.allowEdit(false);
        }
        else
        {
            this.allowEdit(true);
        }
    }
    // </GJP>
    element.enableFieldsActiveReportingCurrency();
    return ret;
}

public boolean validateDelete()
{
    boolean isValid = true;

    if (ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityDeleteEvent())
    {
        // Only perform the validation if called from the LedgerJournalTrans datasource's delete event.

        // Call LedgerJournalTrans_Asset.validateDelete directly as we don't want an extra delete
        // confirmation dialog to be shown.
        isValid = ledgerJournalTrans_Asset.validateDelete();
    }

    return isValid;
}

public void delete()
{
    if (ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityDeleteEvent())
    {
        // Only perform the deletion if called from the LedgerJournalTrans datasource's delete event.

        // Call LedgerJournalTrans_Asset.delete directly as super calls validateWrite, which will cause
        // an extra delete confirmation dialog to be shown.
        ledgerJournalTrans_Asset.delete();
    }
}

/// <summary>
/// Determines whether the current record is valid and ready to be written to the database.
/// </summary>
/// <returns>
/// true if the record is valid; otherwise, false.
/// </returns>
/// <remarks>
/// This method performs the required reason code validation.
/// </remarks>
public boolean validateWrite()
{
    boolean isValid = true;

    if (ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityWriteEvent())
    {
        // Only perform the validation if called from the LedgerJournalTrans datasource's write event.
        isValid = super();
    }

    return isValid;
}

