public void modified()
{
    boolean postToGeneralLedger = (ledgerJournalTable.CurrentOperationsTax != CurrentOperationsTax::None);

    // <GJP>
    if (element.isAssetDocumentTrans_JP())
    {
        element.clearAssetDocument_JP();
    }
    // </GJP>

    if (postToGeneralLedger)
    {
        ledgerJournalEngine.initFromAssetTable(ledgerJournalTrans, ledgerJournalTrans_Asset);

        ledgerJournalEngine.assetBookIdModified(ledgerJournalTrans, ledgerJournalTrans_Asset);
    }
    else
    {
        ledgerJournalTrans.OffsetLedgerDimension = 0;
    }

    super();

    ledgerJournalEngine.setAssetDepStartDate(ledgerJournalTrans, ledgerJournalTrans_Asset);

    element.enableDepreciationStartDate();

    // <GIN>
    if (isDepIncomeTaxActEnabled)
    {
        element.visibleAccountNum_IN();
    }
    // </GIN>

    // <GJP>
    if (LedgerJournalTrans_Asset.isAssembling_JP())
    {
        info(strFmt("@SYS4110666", LedgerJournalTrans_Asset.AssetId));
    }
    if (LedgerJournalTrans_Asset.isDisassembling_JP())
    {
        info(strFmt("@SYS4110667", LedgerJournalTrans_Asset.AssetId));
        element.setDisassemblingAmount_JP();
    }
    // </GJP>

    element.modifyDisposalAmount_PL();
}

