			    }
			}

public void modified()
{
    AmountMST   netBookValue,
                amountCredit;

    super();

    if (ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DisposalScrap
        && ledgerJournalTrans_Asset.BookId
        && ledgerJournalTrans.parmAccount()
        && ledgerJournalTrans.AmountCurCredit)
    {
        netBookValue = AssetSumCalc_Trans::newAssetYear(ledgerJournalTrans.parmAccount(),
                                                        ledgerJournalTrans_Asset.BookId,
                                                        ledgerJournalTrans.TransDate).netBookValue();

        amountCredit = ledgerJournalTrans_Asset.SaleFactor_PL * netBookValue / 100;

        ledgerJournalTrans.AmountCurCredit = CurrencyExchangeHelper::amount(amountCredit, ledgerJournalTable.CurrencyCode);
    }
}

void modified()
{
    ledgerJournalEngine.assetTransTypeModified(ledgerJournalTrans, ledgerJournalTrans_Asset);

    super();

    switch (ledgerJournalTrans_Asset.TransType)
    {
        case AssetTransTypeJournal::Acquisition:
        case AssetTransTypeJournal::AcquisitionAdj:
        case AssetTransTypeJournal::WriteUpAdj:
            debCredProposal  = DebCredProposal::Debit;
            break;

        case AssetTransTypeJournal::Depreciation:
        case AssetTransTypeJournal::DepreciationAdj:
        case AssetTransTypeJournal::WriteDownAdj:
        case AssetTransTypeJournal::DisposalSale:
        case AssetTransTypeJournal::DisposalScrap:
            debCredProposal  = DebCredProposal::Credit;
            break;

        case AssetTransTypeJournal::Revaluation:
            debCredProposal  = DebCredProposal::None;
            break;
    }

    var eventHandlerResult = new EventHandlerResult();
    LedgerJournalEngine::callGetDebCredProposalByAssetTransTypeJournalDelegate(ledgerJournalTrans_Asset.TransType, eventHandlerResult);

    if (eventHandlerResult.hasResult())
    {
        debCredProposal = eventHandlerResult.result();
    }

    element.setDebCredProposal(false);
    element.enableDepreciationStartDate();

    LedgerJournalEngine::createDefsForAssetBookTableDerivedJourn(ledgerJournalTrans, ledgerJournalTrans_Asset);

    // <GEEPL>
    element.updateSaleFactorField();
    // </GEEPL>

    // <GIN>
    if (isDepIncomeTaxActEnabled)
    {
        element.visibleAccountNum_IN();
    }
    // </GIN>

    element.modifyDisposalAmount_PL();

    if (isIsoDEAT)
    {
        element.enableFieldsActive();
    }           

    element.setReportingCurrencyAmountsForDepreciation();
    AssetBookTableDerivedJournal_ds.research();
}

