/// <summary>
/// Constructs a lookup for the reason code field.
/// </summary>
/// <remarks>
/// The lookup shows the appropriate reason codes (if any) given the offset and main account selections.
/// </remarks>
public void lookup()
{
    LedgerJournalACType   offsetACType;
    LedgerJournalACType   ledgerJournalACType;

    if (ledgerJournalTrans.parmOffsetLedgerDimension())
    {
        offsetACType = ledgerJournalTrans.OffsetAccountType;
    }
    else
    {
        offsetACType = LedgerJournalACType::Ledger;
    }

    ledgerJournalACType = ledgerJournalTrans.AccountType;

    ReasonFormTable::reasonCodeLookupMultipleAccounts(this, offsetACType, ledgerJournalACType, true, true);
}

public boolean modified()
{
    boolean ret;

    ret = super();            

    LedgerJournalTrans_Asset::setReportingCurrencyAmountsForDepreciation(ledgerJournalTrans, ledgerJournalTrans_Asset);

    return ret;
}

public void lookup()
{
    AssetGroupBookSetup assetGroupBookSetupLoc;

    if (AssetParameters::checkAssetParameters_IN()                                 &&
        (ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::Depreciation ||
        ledgerJournalTrans_Asset.TransType == AssetTransTypeJournal::DepreciationAdj))
    {
        assetGroupBookSetupLoc = AssetGroupBookSetup::find(ledgerJournalTrans_Asset.AssetGroup_IN, ledgerJournalTrans_Asset.BookId);
        if (assetGroupBookSetupLoc.Depreciation == NoYes::Yes)
        {
            AssetGroupBookSetup::derivedBookIdDepreciatelookup_IN(this, ledgerJournalTrans_Asset.AssetGroup_IN, assetGroupBookSetupLoc.Depreciation);
        }
        else
        {
            AssetGroupBookSetup::derivedBookIdGroupDepreciatelookup_IN(this, ledgerJournalTrans_Asset.AssetGroup_IN, assetGroupBookSetupLoc.AssetGroupDepreciation_IN);
        }
    }
    else
    {
        super();
    }
}
