public void initPuttingIntoOperationValue()
{
    if (ledgerJournalTrans_RAsset.TransType == RAssetTransType::Acquisition
        && ledgerJournalTrans_RAsset.AssetId
        && ledgerJournalTrans_RAsset.StandardId
        && !ledgerJournalTrans.AmountCurDebit
        && ledgerJournalTrans_RAsset.BudgetModel)
    {
        RAssetproposalAcquisition rAssetproposalAcquisition = RAssetProposal::construct(ledgerJournalTrans_RAsset.TransType, '', true);
        rAssetproposalAcquisition.initParameters(ledgerJournalTrans.JournalNum,
            ledgerJournalTrans.TransDate,
            RAssetStandards::find(ledgerJournalTrans_RAsset.AssetId, ledgerJournalTrans_RAsset.StandardId),
            '', true);

        ledgerJournalTrans.AmountCurDebit = rAssetproposalAcquisition.currentAcquisitionValue(ledgerJournalTrans_RAsset.StandardId);
    }
}

