public edit TaxAmountJournal correctedTaxAmount(boolean _set, LedgerJournalTrans _ledgerJournalTrans, TaxAmountCur _taxAmountCur)
{
    TaxAmountCur taxAmountCur = _taxAmountCur;

    if (_ledgerJournalTrans.RecId
        && !ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityWriteEvent())
    {
        if (_set)
        {
            ledgerJournalEngine.correctTaxAmount(_ledgerJournalTrans, taxAmountCur);
            ledgerJournalEngine.cashDiscCodeModified(_ledgerJournalTrans);
        }
        else
        {
            taxAmountCur = ledgerJournalEngine.correctedTaxAmount(_ledgerJournalTrans);
        }
    }
    else
    {
        if (_set)
        {
            ledgerJournalEngine.correctTaxAmount(_ledgerJournalTrans, taxAmountCur);
            ledgerJournalEngine.cashDiscCodeModified(_ledgerJournalTrans);
        }
        else
        {
            taxAmountCur = correctedTaxAmountCur;
        }
    }

    correctedTaxAmountCur = taxAmountCur;

    return taxAmountCur;
}

