public boolean validateWrite()
{
    ReasonRefRecID reasonRef = ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, ReasonRefRecID))
        .getValue();

    boolean ret = super();

    if (ret
        && !ledgerJournalEngine.formMethodDataSourceValidateWritePost())
    {
        ret = false;
    }

    ReasonTableRef reasonTableRef = ReasonTableRef::find(reasonRef);

    if (ret
        && (!reasonRef
            || !reasonTableRef.Reason))
    {
        if (RAssetParameters::transTypeRequiresValidation(ledgerJournalTrans_RAsset_ds.object(fieldNum(LedgerJournalTrans_RAsset, TransType)).getValue()))
        {
            ret = checkFailed(strFmt("@SYS110217", "@SYS111246"));
        }
    }
    else if (ret
        && reasonTableRef.Reason)
    {
        LedgerJournalACType offsetACType = ledgerJournalTrans.parmOffsetLedgerDimension()
            ? ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, OffsetAccountType)).getValue()
            : LedgerJournalACType::Ledger;

        ReasonTable reasonTable = ReasonTable::findReasonByFilter(reasonTableRef.Reason, LedgerJournalACType::FixedAssets_RU, true, offsetACType, true);

        if (!reasonTable)
        {
            ret = checkFailed("@SYS115522");
        }
    }

    return ret;
}

