public void write()
{
    boolean discCodeModified = (ledgerJournalTrans.orig().CashDiscCode != ledgerJournalTrans.CashDiscCode);
    boolean discDetailModified = (ledgerJournalTrans.orig().DateCashDisc != ledgerJournalTrans.DateCashDisc
        || ledgerJournalTrans.orig().CashDiscAmount != ledgerJournalTrans.CashDiscAmount);
    boolean isNewRecord = !ledgerJournalTrans.RecId;

    // The entity is already being written. The extra call to write is unnecessary.
    if (!ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityDeleteEvent()
        && !ledgerJournalTransEntityManager.getFormDataEventManager().isInEntityWriteEvent())
    {
        try
        {
            ttsbegin;

            // Signal the start of the write event.
            ledgerJournalTransEntityManager.getFormDataEventManager()
                .beginEntityWriteEvent();

            // Clear out any old snapshots for the write event.
            ledgerJournalTransEntityManager.getInMemoryRollbackManager()
                .clearSnapShots();

            ledgerJournalEngine.preWrite(ledgerJournalTrans);
            ledgerJournalEngine.preWriteUpdateDefaultDimension(ledgerJournalTrans, null, ledgerJournalTrans_RAsset);
            ledgerJournalEngine.formMethodDataSourceWritePre();

            if (!ledgerJournalTrans.parmOffsetLedgerDimension())
            {
                // The offset account is empty so attempt to default one in.
                ledgerJournalTrans.parmOffsetLedgerDimension(
                    ledgerJournalTrans.getLedgerDimensionForLedgerType(
                        ledgerJournalEngine.assetOffsetLedgerDimension_RU(ledgerJournalTrans, ledgerJournalTrans_RAsset),
                        ledgerJournalTrans.getOffsetCompany()));
                ledgerJournalTrans.OffsetAccountType = LedgerJournalACType::Ledger;
            }

            // -----------------------------------------------------------------------------------
            // Validate and write the entire entity. This must be done because to maintain data integrity
            // as we require an "all or nothing" behavior.
            // -----------------------------------------------------------------------------------

            // A snapshot of the current buffer is taken to support in-memory rollback of the buffer in
            // the case the transaction is aborted.
            ledgerJournalTransEntityManager.getInMemoryRollbackManager()
                .takeSnapshotOfLedgerJournalTrans();
            // If super() is called directly, the client currently refreshes all of the linked datasources,
            // which causes the LedgerJournalTrans_Asset datasource to be corrupted. The result is the
            // changes to the LedgerJournalTrans_Asset table are not saved. The long term plan is to replace
            // the LedgerJournalTransEntityManager with unit of work.
            // super(); // the next line replaces this one
            ledgerJournalTrans.write();

            if (ledgerJournalTrans.isFixedAssetsTransaction_RU())
            {
                ledgerJournalTransEntityManager.getInMemoryRollbackManager()
                    .takeSnapshotOfLedgerJournalTrans_RAsset();

                ledgerJournalTransEntityManager.getFormDataEventManager()
                    .validateAndWriteRAssetsSibling_RU();
            }
            else
            {
                ledgerJournalTrans_RAsset.RecId = 0;
            }

            if (isNewRecord)
            {
                ledgerJournalEngine.correctTaxAmount(ledgerJournalTrans, correctedTaxAmountCur);
            }

            switch (ledgerJournalTrans.AccountType)
            {
                case LedgerJournalACType::Cust:
                case LedgerJournalACType::Vend:
                    if (discCodeModified
                        || discDetailModified)
                    {
                        if (ledgerJournalTrans.CashDiscCode)
                        {
                            ledgerJournalEngine.cashDiscCodeModified(ledgerJournalTrans, null, cashDiscChanged);
                        }
                        else
                        {
                            ledgerJournalEngine.deleteCashDisc(ledgerJournalTrans);
                        }
                    }
                    break;
            }

            ledgerJournalEngine.parmLedgerJournalTransRAsset(ledgerJournalTrans_RAsset);
            ledgerJournalEngine.write(ledgerJournalTrans, cashDiscChanged, null, null, null);
            journalFormTrans.refreshInvisible();

            ledgerJournalTransEntityManager.getFormDataEventManager()
                .endEntityWriteEvent();

            ttscommit;
        }
        catch (Exception::UpdateConflict)
        {
            // This exception should not be handled by the generic handling logic below, as it assumes
            // that a rollback needs to be performed.
            // A rollback should not be performed for an update conflict, as the record will never
            // be capable of being saved.

            // Signal that the write event is finished with a failure by calling endEntityWriteEvent
            // and passing in 'true' for the failure parameter.
            ledgerJournalTransEntityManager.getFormDataEventManager()
                .endEntityWriteEvent(true);

            // Chain the exception forward.
            throw Exception::UpdateConflict;
        }

        catch
        {
            // Handle any exception and then chain another generic one forward to force the form event to fail. Also, in-memory roll-backs
            // of the transaction buffers must be handled as the kernel does not perform this automatically.
            ledgerJournalTransEntityManager.getInMemoryRollbackManager()
                .performRollbackForAbortedWrite();

            // Signal that the write event is finished.
            ledgerJournalTransEntityManager.getFormDataEventManager()
                .endEntityWriteEvent();

            // Signal that the write event is finished with a failure by calling endEntityWriteEvent
            // and passing in 'true' for the failure parameter.
            ledgerJournalTransEntityManager.getFormDataEventManager()
                .endEntityWriteEvent(true);


            throw Exception::Error;
        }
    }
}

