public void delete()
{
    try
    {
        ttsbegin;

        ledgerJournalTransEntityManager.getFormDataEventManager()
            .beginEntityDeleteEvent();

        ledgerJournalTransEntityManager.getInMemoryRollbackManager()
            .clearSnapShots();

        LedgerJournalTrans ledgerJournalTransCopy = ledgerJournalTrans.data();
        ledgerJournalEngine.rereadWhenDirty(ledgerJournalTrans, true);

        ledgerJournalEngine.preDelete(ledgerJournalTrans);
        ledgerJournalEngine.formMethodDataSourceDeletePre();

        if (ledgerJournalTrans_RAsset.RefRecId == ledgerJournalTrans.RecId
            && !ledgerJournalTrans_RAsset_DS.validateDelete())
        {
            // The sibling table is invalid, so throw an exception to prevent actions like "form closing" from finishing
            throw Exception::Error;
        }

        ledgerJournalTransEntityManager.getInMemoryRollbackManager()
            .takeSnapshotOfLedgerJournalTrans();

        super();

        ledgerJournalEngine.delete(ledgerJournalTransCopy);

        if (ledgerJournalTrans)
        {
            element.setDebCredProposal(true);
        }

        ledgerJournalTransEntityManager.getFormDataEventManager()
            .endEntityDeleteEvent();

        ttscommit;
    }
    catch
    {
        ledgerJournalTransEntityManager.getInMemoryRollbackManager()
            .performRollbackForAbortedDelete();

        ledgerJournalTransEntityManager.getFormDataEventManager()
            .endEntityDeleteEvent(true);

        throw Exception::Error;
    }
}

public void create(boolean _append = false)
{
    try
    {
        ledgerJournalTransEntityManager.getFormDataEventManager()
            .beginEntityCreateEvent();

        journalFormTrans.datasourceCreatePre();

        element.setDebCredProposal(true);

        if (element.args().dataset() == tableNum(LedgerJournalTable))
        {
            Args args = new Args(element);
            args.record(ledgerJournalTable);
            RAssetJournalAdd::main(args);
            ledgerJournalEngine.ledgerJournalTableUpdateTotals(ledgerJournalTable, true);
            this.research();
        }

        virtualCashDiscDate = dateNull();
        virtualCashDiscAmount = 0;
        cashDiscChanged = false;

        // Signal that the create event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager()
            .endEntityCreateEvent();

    }
    catch (Exception::UpdateConflict)
    {
        // This exception should not be handled by the generic handling logic below.

        // Signal that the create event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager()
            .endEntityCreateEvent();

        // Chain the exception forward.
        throw Exception::UpdateConflict;
    }
    catch
    {
        // Signal that the create event is finished.
        ledgerJournalTransEntityManager.getFormDataEventManager()
            .endEntityCreateEvent();

        throw Exception::Error;
    }
}

