public display TaxAmountJournal taxAmountJournal(LedgerJournalTrans _ledgerJournalTrans)
{
    TaxAmountJournal taxAmountJournal = ledgerJournalEngine.taxAmountJournal(_ledgerJournalTrans);

    if (taxAmountJournal == 0
        || _ledgerJournalTrans.AccountType == LedgerJournalACType::Employee_RU)
    {
        correctedTaxAmount.allowEdit(false);
        correctedTaxAmount.skip(true);

        if (correctedTaxAmountCur != 0)
        {
            correctedTaxAmountCur = 0;
            ledgerJournalEngine.correctTaxAmount(_ledgerJournalTrans, 0);
        }
    }
    else
    {
        correctedTaxAmount.allowEdit(true);
        correctedTaxAmount.skip(false);
    }

    return taxAmountJournal;
}

