/// <summary>
/// Sets up and executes a query that will pull back all of the records from the ReasonTable
/// table that have bank and either customer or ledger defined in the reason code setup.
/// </summary>
public void lookupReasonCode()
{
    // Add the fields to be shown in the lookup form
    reasonCodeLookup.addLookupfield(fieldNum(ReasonTable, Reason));
    reasonCodeLookup.addLookupfield(fieldNum(ReasonTable, Description));

    // Add the query to the lookup form
    reasonCodeLookup.parmQuery(journalFormTrans.getReasonQuery());

    // Perform lookup
    reasonCodeLookup.performFormLookup();
}

