    return ret;
}

public boolean validateDelete()
{
    boolean isValid = true;
    if (!super())
    {
        // The LedgerJournalTrans' delete method requires an exception to be thrown
        // so that the transaction is aborted.
        throw Exception::Error;
    }

    return isValid;
}

