public void clicked()
{
    BankAccountTrans    bankAccountTrans;
    FormRun             formRun;
    Args                args = new Args();
    super();

    bankAccountTrans = BankAccountTrans::find(ledgerJournalTrans.PaymReference);

    args.record(bankAccountTrans);
    args.caller(element);

    formRun = new MenuFunction(menuitemDisplayStr(BankAccountTrans),MenuItemType::Display).create(args);

    if (formRun)
    {
        formRun.run();
        formRun.wait();
    }
}

public void clicked()
{
    FormRun     formRun;
    Args        args = new Args();
    boolean     end  = false;

    super();

    if (!ledgerJournalTrans.parmLedgerDimension())
    {
        throw error("@SYS25989");
    }

    args.record(ledgerJournalTrans);

    switch (ledgerJournalTrans.AccountType)
    {
        case LedgerJournalACType::Cust :
            formRun = new MenuFunction(menuitemDisplayStr(BankShowSpecLdgrJourTrnBankDepCanc),MenuItemType::Display).create(args);
            break;

        case LedgerJournalACType::Vend :
            formRun = new MenuFunction(menuitemDisplayStr(VendShowSpecifikation),MenuItemType::Display).create(args);
            break;

        default:
            end = true;
    }

    if (!end)
    {
        if (formRun)
        {
            formRun.run();
            formRun.wait();
        }
    }
}

