/// <summary>
/// Performs the basic initialization of the form.
/// </summary>
public void init()
{
    chineseVoucher_CN = LedgerParameters::isChineseVoucher_CN();
    journalFormTrans = new LedgerJournalFormTrans_Payment(element);

    super();

    LedgerJournalTrans_AccountNum.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_DS, fieldStr(LedgerJournalTrans, OffsetLedgerDimension), fieldStr(LedgerJournalTrans, OffsetAccountType)));
    LedgerJournalTrans_AccountNum1.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_DS, fieldStr(LedgerJournalTrans, OffsetLedgerDimension), fieldStr(LedgerJournalTrans, OffsetAccountType)));
    fieldGridOffsetAccount.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_DS, fieldStr(LedgerJournalTrans, LedgerDimension), fieldStr(LedgerJournalTrans, AccountType)));
    LedgerJournalTrans_OffsetAccount1.parmDimensionAutocompleteFilter(new DimensionAutocompleteVendDefaultFilter(ledgerJournalTrans_DS, fieldStr(LedgerJournalTrans, LedgerDimension), fieldStr(LedgerJournalTrans, AccountType)));

    journalFormTrans.parmCtrlVoucherGrid(ledgerJournalTrans_Voucher);
    journalFormTrans.parmCtrlVoucherTab(ledgerJournalTrans_Voucher1);
    journalFormTrans.parmCtrlTransDateGrid(ledgerJournalTrans_TransDate);
    journalFormTrans.parmCtrlTransDateTab(ledgerJournalTrans_TransDate1);
    journalFormTrans.parmCtrlSettlementButton(buttonSettlement);
    journalFormTrans.setCtrlSelectSettledBillsButton(SelectSettledBillsButton);
    journalFormTrans.parmCtrlCourseButton(buttonCourse);
    journalFormTrans.parmCtrlReportAsReadyButton(buttonReportAsReady);
    journalFormTrans.parmCtrlApproveButton(buttonApprove);
    journalFormTrans.parmCtrlRejectButton(buttonReject);
    journalFormTrans.parmCtrlPostJournalButton(postJournal);
    journalFormTrans.parmCtrlPostJournalBatchButton(postJournalBatch);
    journalFormTrans.parmCtrlPostAndTransferJournalButton(ledgerJournalPostTransfer);
    journalFormTrans.parmCtrlCheckJournalMenu(buttonCheckJournal);
    journalFormTrans.parmCtrlVoucherTransButton(buttonVoucherTrans);
    journalFormTrans.parmCtrlDeleteTransactionsButton(buttonDeleteTransactions);
    journalFormTrans.parmCtrlBalanceControButton(buttonBalanceControl);
    journalFormTrans.parmCtrlGetTransactionsButton(buttonGetTransactions);
    journalFormTrans.parmCtrlFunctionMenuButton(buttonFunctions);
    journalFormTrans.parmCtrlCreatePaymentButton(buttonCreatePayments);
    journalFormTrans.parmCtrlPaymentReconciliationButton(buttonPaymReconciliation);
    journalFormTrans.parmCtrlPaymentProposalButton(paymProposal);
    journalFormTrans.parmCtrlCancelNoteOrDocumentButton(bankBillOfExchangeDocumentCancel);
    journalFormTrans.parmCtrlPaymentStatusMenuButton(buttonPaymReconciliation);
    journalFormTrans.parmCtrlPaymentProposalMenuButton(paymProposal);
    journalFormTrans.parmCtrlSalesTaxMenu(taxTransSource);
    journalFormTrans.parmCtrlAccountDimensionButton(accountDimension);
    journalFormTrans.parmCtrlOffsetAccountDimensionButton(offsetAccountDimension);
    journalFormTrans.parmCtrlPaymFeeAccountDimensionButton(paymentFeeAccountDimension);
    journalFormTrans.parmCalculatedTaxAmount(taxAmountJournal1);
    journalFormTrans.parmActualTaxAmount(correctedTaxAmount);
    journalFormTrans.parmCtrlErrorExists(fieldError);
    journalFormTrans.parmCtrlReportingExchRate(reportingfieldExchRate);
    journalFormTrans.parmCtrlReportingExchRateSecond(reportingfieldExchRateSecond);

    journalFormTrans.formInitPost();

    element.initCaption();
    this.initLedger();

    TaxVATNumTable::enableLookupVATNum(fieldVatNumJournal);
    cashDiscChanged = false;

    imageListAppl = new ImageListAppl_LedgerJournal();
    fieldError.imageList(imageListAppl.imageList());

    lastEnteredReleaseDate = utcDateTimeNull() ;
    this.enableButtons();
    LedgerJournalTrans_TransDate.setFocus();

    if (TaxThaiGovCertificationFeatureChecker::isUnrealizedVATEnabled())
    {
        this.updateDesignForUnrealizedVAT();
    }

    if (chineseVoucher_CN)
    {
        boolean allow = ledgerJournalTable.ledgerJournalName().NewVoucher == NewVoucher::Manually
            && ledgerJournalTable.VoucherAllocatedAtPosting == NoYes::No;
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher_CN)).enabled(allow);
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher)).enabled(false);
    }
    else if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoCN]))
    {
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, Voucher_CN)).visible(false);
        ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, LedgerVoucherType_CN)).visible(false);
    }

    boolean useReportingCurrency = Ledger::reportingCurrency() != '';
    totalsGroupTabularReportingCurrency.visible(useReportingCurrency);
    accountingCurrencytotalHeaderCaption.visible(useReportingCurrency);

    if (BankPaymAdviceReportV2FeatureExposure::isEnabled())
    {
        BankPaymAdviceCustV2.visible(true);
        BankPaymAdviceCustPrint.visible(false);
    }

    if (BrazilParameters::isEnabled())
    {
        BankPaymAdviceCustPrint.visible(false);
        BankPaymAdviceCustV2.visible(false);
    }

    this.initFinancialTags();
}

private void initFinancialTags()
{
    if (FinTagConfiguration::isFinTagConfigurationSetInAtLeastOneCompany() && CustBillOfExchangeFinTagFlight::instance().isEnabled())
    {
        FinTagReferenceGroupControllerContract primaryContract = FinTagReferenceGroupControllerContract::construct(
            LedgerJournalTrans_FinTag,
            fieldNum(LedgerJournalTrans, Company));

        FinTagReferenceGroupController::registerReferenceGroup(primaryContract);
        LedgerJournalTrans_FinTag.visible(true);
    }
}

private void initOffsetFinancialTags()
{
    if (FinTagConfiguration::isFinTagConfigurationSetInAtLeastOneCompany() && CustBillOfExchangeFinTagFlight::instance().isEnabled())
    {
        FinTagReferenceGroupControllerContract offsetContract = FinTagReferenceGroupControllerContract::construct(
            LedgerJournalTrans_OffsetFinTag,
            fieldNum(LedgerJournalTrans, OffsetCompany));

        FinTagReferenceGroupController::registerReferenceGroup(offsetContract);
        LedgerJournalTrans_OffsetFinTag.visible(true);
    }
}

/// <summary>
/// Enables/disables certain controls based on state and journal type.
/// </summary>
public void initButtons()
{
    if (ledgerJournalTable.JournalType == LedgerJournalType::CustBillOfExchangeDraw
        || ledgerJournalTable.JournalType == LedgerJournalType::CustBillOfExchangeRedraw)
    {
        bankRemittance.visible(false);
    }

    btnPaymFeeInquiry.visible(ledgerJournalTable.JournalType == LedgerJournalType::CustPaymRemittance);
}

private void initIntracomVATDueDate()
{
    ledgerJournalTrans_DS.object(fieldNum(LedgerJournalTrans, IntraComVATDueDate_W)).enabled(VendParameters::find().DocumentDateForIntracomVAT_W);
}

private void initCaption()
{
    if (element.args().dataset() == tableNum(LedgerJournalTable))
    {
        ledgerJournalTable = element.args().record();
        switch (ledgerJournalTable.JournalType)
        {
            case LedgerJournalType::CustBillOfExchangeDraw:
                element.design().caption("@CashManagement:DrawBOEJournalCaption");
                break;

            case LedgerJournalType::CustBillOfExchangeProtest:
                element.design().caption("@CashManagement:ProtestBOEJournalCaption");
                break;

            case LedgerJournalType::CustBillOfExchangeRedraw:
                element.design().caption("@CashManagement:RedrawBOEJournalCaption");
                break;

            case LedgerJournalType::CustPaymRemittance:
                element.design().caption("@CashManagement:RemittanceJournalCaption");
                break;

            case LedgerJournalType::CustBillOfExchangeSettle:
                element.design().caption("@CashManagement:SettleBOEJournalCaption");
                break;

            default:
                break;
        }
    }
}

public void initLedger()
{
    TransDate dateFrom = dateNull();
    TransDate dateTo = DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone());

    if (element.args().dataset() == tableNum(LedgerJournalTable))
    {
        ledgerJournalTable = element.args().record();
        journalNum = ledgerJournalTable.JournalNum;

        LedgerJournalTrans_AccountNum.parmJournalName(ledgerJournalTable.JournalName);
        LedgerJournalTrans_AccountNum1.parmJournalName(ledgerJournalTable.JournalName);

        fieldGridOffsetAccount.parmJournalName(ledgerJournalTable.JournalName);
        LedgerJournalTrans_OffsetAccount1.parmJournalName(ledgerJournalTable.JournalName);

        CustVendPaymJournalFee_CustVendAC.parmJournalName(ledgerJournalTable.JournalName);

        switch (ledgerJournalTable.JournalType)
        {
            case LedgerJournalType::CustBillOfExchangeDraw:
                bankBillOfExchangeDocumentCancel.visible(true);
                groupBillofexchange_BankAccountId.visible(true);
                ledgerJournalTrans_PaymentStatus.visible(true);
                buttonPaymReconciliation.visible(true);
                break;

            case LedgerJournalType::CustBillOfExchangeProtest:
                paymentFee.visible(true);
                paymentFeeAccountDimension.visible(true);
                groupBillOfExchange_protestReason.visible(true);
                groupProtest.visible(true);
                groupBillofexchange_BankRemittanceType.visible(true);
                groupBillofexchange_BankAccountId.visible(true);
                if (ledgerJournalTable.ProtestSettledBill == NoYes::Yes)
                {
                    fieldGridOffsetType.visible(true);
                    fieldGridOffsetAccount.visible(true);
                    offsetAccountDimension.visible(true);
                    this.initOffsetFinancialTags();
                    ledgerJournalTrans_OffsetAccountType1.visible(true);
                    ledgerJournalTrans_OffsetAccount1.visible(true);
                    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, OffsetLedgerDimension)).allowEdit(true);
                    ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, OffsetAccountType)).allowEdit(true);
                    buttonSettlement.visible(false);
                    SelectSettledBillsButton.visible(true);
                    paymProposal.visible(false);
                }
                break;

            case LedgerJournalType::CustBillOfExchangeRedraw:
                bankBillOfExchangeDocumentCancel.visible(true);
                groupBillofexchange_BankAccountId.visible(true);
                ledgerJournalTrans_PaymentStatus.visible(true);
                buttonPaymReconciliation.visible(true);
                break;

            case LedgerJournalType::CustPaymRemittance:
                groupBillofexchange_BankRemittanceType.visible(true);
                groupBillofexchange_BankAccountId.visible(true);
                groupBillofexchange_BankAccountId.allowEdit(false);
                paymentFee.visible(true);
                paymentFeeAccountDimension.visible(true);
                ledgerJournalTrans_PaymentStatus.visible(true);
                buttonPaymReconciliation.visible(true);
                buttonCreatePayments.text("@SYS83541");
                break;

            case LedgerJournalType::CustBillOfExchangeSettle:
                groupBillofexchange_BankRemittanceType.visible(true);
                groupBillofexchange_BankAccountId.visible(true);
                fieldGridOffsetType.visible(true);
                fieldGridOffsetAccount.visible(true);
                offsetAccountDimension.visible(true);
                this.initOffsetFinancialTags();
                ledgerJournalTrans_OffsetAccountType1.visible(true);
                ledgerJournalTrans_OffsetAccount1.visible(true);
                ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, OffsetLedgerDimension)).allowEdit(true);
                ledgerJournalTrans_ds.object(fieldNum(LedgerJournalTrans, OffsetAccountType)).allowEdit(true);
                paymentFee.visible(true);
                paymentFeeAccountDimension.visible(true);
                break;
        }

        this.setAllowEditRemittanceType();

        if (ledgerJournalTable.FixedOffsetAccount && ledgerJournalTable.parmOffsetLedgerDimension())
        {
            switch (ledgerJournalTable.OffsetAccountType)
            {
                case LedgerJournalACType::Ledger:
                    if (DimensionFocusBalanceUpdateByLedgerFlight::instance().isEnabled())
                    {
                        MainAccountEventArgs args = MainAccountEventArgs::construct();
                        args.parmBalanceLedgerRecId(Ledger::current());

                        MainAccount::updateBalancesWithArgs(args);
                    }
                    else
                    {
                        MainAccount::updateBalances();
                    }

                    ledgerBalance = LedgerBalanceMainAccountAmounts::construct();
                    ledgerBalance.parmAccountingDateRange(dateFrom, dateTo);
                    ledgerBalance.parmIncludeRegularPeriod(true);
                    ledgerBalance.parmIncludeClosingPeriod(true);
                    ledgerBalance.calculateBalance(LedgerDimensionFacade::getMainAccountFromLedgerDimension(ledgerJournalTable.OffsetLedgerDimension));
                    balanceAmountMST = ledgerBalance.getAccountingCurrencyBalance();
                    break;
                case LedgerJournalACType::Bank:
                    balanceAmountMST = BankAccountTrans::sumTransMST(ledgerJournalTable.parmOffsetAccount());
                    break;
                case LedgerJournalACType::Cust:
                    balanceAmountMST = CustTrans::balanceMST(ledgerJournalTable.parmOffsetAccount());
                    break;
                case LedgerJournalACType::Vend:
                    balanceAmountMST = VendTrans::balanceMST(ledgerJournalTable.parmOffsetAccount());
                    break;
            }
            offsetBalance.visible(true);
            ledgerJournalTrans_OffsetAccountType1.allowEdit(false);
            ledgerJournalTrans_OffsetAccount1.allowEdit(ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger);
            ledgerJournalTrans_OffsetCompany.allowEdit(false);
        }
        else
        {
            offsetBalance.visible(false);
        }

        // Lock the main account segment if "Fixed offset account" is selected in Journal Names
        if (ledgerJournalTrans.OffsetAccountType == LedgerJournalACType::Ledger)
        {
            LedgerJournalEngine::updateSegmentedEntryForFixedOffsetAccount(fieldGridOffsetAccount, ledgerJournalTable, ledgerJournalTrans);
            LedgerJournalEngine::updateSegmentedEntryForFixedOffsetAccount(LedgerJournalTrans_OffsetAccount1, ledgerJournalTable, ledgerJournalTrans);
        }
    }
}

public JournalForm journalForm()
{
    return journalFormTrans;
}

public LedgerJournalEngine ledgerJournalEngine()
{
    return ledgerJournalEngine;
}

public LedgerJournalTable ledgerJournalTable()
{
    return ledgerJournalTable;
}

public void refreshProposal()
{
    ledgerJournalTrans_DS.research();
    element.bookRefresh();
}

public void research()
{
    ledgerJournalEngine.newJournalActive(ledgerJournalTable, true);
    CustVendPaymProposalLine::deleteByJournalId(ledgerJournalTable.JournalNum);
    ledgerJournalTrans_ds.research();
}

public void setAllowEditRemittanceType()
{
    groupBillOfExchange_BankRemittanceType.allowEdit(
        ledgerJournalTrans.ledgerJournalTable().JournalType != LedgerJournalType::CustPaymRemittance
            && ledgerJournalTrans.SettleVoucher != SettlementType::SelectedTransact);
}

public void enableBankPaymAdviceCustPrint()
{
    boolean enabled = false;

    if (ledgerJournalTrans.PaymentStatus == CustVendPaymStatus::Sent
        && ledgerJournalTable.JournalType == LedgerJournalType::CustBillOfExchangeDraw)
    {
        enabled = true;
    }

    if (enabled)
    {
        enabled = journalFormTrans.isJournalEnabled();
    }

    BankPaymAdviceCustPrint.enabled(enabled);
    BankPaymAdviceCustV2.enabled(enabled);
}

public void setDebCredProposal(boolean _init)
{
    if (_init)
    {
        debCredProposal = DebCredProposal::None;
    }

    if (debCredProposal != debCredProposalOld)
    {
        switch (debCredProposal)
        {
            case DebCredProposal::Credit:
                ledgerJournalTrans_AmountCurDebit.skip(true);
                fieldAmountCurDebit.skip(true);
                break;

            case DebCredProposal::Debit:
                ledgerJournalTrans_AmountCurCredit.skip(true);
                fieldAmountCurCredit.skip(true);
                break;

            case DebCredProposal::None:
                ledgerJournalTrans_AmountCurCredit.skip(false);
                fieldAmountCurCredit.skip(false);
                ledgerJournalTrans_AmountCurDebit.skip(false);
                fieldAmountCurDebit.skip(false);
                break;
        }

        debCredProposalOld = debCredProposal;
    }
}

/// <summary>
/// Sets fields not visible.
/// </summary>
private void updateDesignForUnrealizedVAT()
{
    LedgerJournalTransExtensionTH_ds.object(fieldNum(LedgerJournalTransExtensionTH, InvoiceReceiptDate)).visible(false);
    LedgerJournalTransExtensionTH_ds.object(fieldNum(LedgerJournalTransExtensionTH, TaxInvoiceReceiptDate)).visible(false);
    LedgerJournalTransExtensionTH_ds.object(fieldNum(LedgerJournalTransExtensionTH, RealVendName)).visible(false);
    LedgerJournalTransExtensionTH_ds.object(fieldNum(LedgerJournalTransExtensionTH, InvoiceDate)).visible(false);
}

