/// <summary>
/// Called when Release date field is modified by the user. Saves the entered value in internal variable.
/// Gives a warning if the date is in the past.
/// </summary>
public void modified()
{
    super();

    if (ledgerJournalTrans.ReleaseDate != utcDateTimeNull()
        && DateTimeUtil::addDays(ledgerJournalTrans.ReleaseDate, 1) < DateTimeUtil::utcNow())
    {
        warning("@SYS114595");
    }

    lastEnteredReleaseDate = ledgerJournalTrans.ReleaseDate;
}

