{
    try
    {
        ledgerJournalEngine.lock();

        CustVendPaymProposalEditForm::newLedgerJournalTrans(ledgerJournalTrans).run();
    }
    finally
    {
        ledgerJournalEngine.unlock();
        ledgerJournalTrans_ds.write();
    }
}

