public void clicked()
{
    LedgerJournalTrans ledgerJournalTransLoc;

    if (element.approvalDateDialog())
    {
        super();

        for (ledgerJournalTransLoc = LedgerJournalTrans_ds.getFirst(true) ? LedgerJournalTrans_ds.getFirst(true) : ledgerJournalTrans;
            ledgerJournalTransLoc;
            ledgerJournalTransLoc = LedgerJournalTrans_ds.getNext())
        {
            ledgerJournalTransLoc.paymStatusModified(approvalDate);
        }

        ledgerJournalTrans_ds.research();
    }
}

public void clicked()
{
    LedgerJournalTrans findRecord;

    findRecord.data(ledgerJournalTrans);

    super();

    element.args().lookupRecord(findRecord);
    ledgerJournalTrans_ds.research();

    paymReconciliationConfirm.enabled(true);
    paymReconciliationRecieved.enabled(true);
    paymReconciliationSent.enabled(true);
}

public void clicked()
{
    super();

    if (!ledgerJournalTrans.parmLedgerDimension())
    {
        throw error("@SYS25989");
    }

    Args args = new Args();
    args.record(ledgerJournalTrans);
    FormRun formRun;

    switch (ledgerJournalTrans.AccountType)
    {
        case LedgerJournalACType::Cust:
            formRun = new MenuFunction(menuItemDisplayStr(CustShowSpecLdgrJourTrnTransCustBOE), MenuItemType::Display).create(args);
            break;

        case LedgerJournalACType::Vend:
            formRun = new MenuFunction(menuItemDisplayStr(VendShowSpecifikation), MenuItemType::Display).create(args);
            break;
    }

    if (formRun)
    {
        formRun.run();
        formRun.wait();
    }
}

