[Form]
public class LedgerJournalTransDailyTAccount extends FormRun
{
    private const str FeatureReference = 'JP-00005';

    LedgerJournalEngine_TAccount            ledgerJournalEngine_TAccount;

    LedgerJournalId                         ledgerJournalId;
    Voucher                                 voucher;

    LedgerJournalTable                      ledgerJournalTable;

    boolean                                 allowEdit;
}

/// <summary>
/// Refreshes <c>LedgerJournalTransDaily</c> form when this form is closed.
/// </summary>
public void bookRefresh()
{
    FormRun callerForm = element.args().caller();

    if (callerForm)
    {
        callerForm.bookRefresh();
        callerForm.initCallerLink();
        callerForm.clearTaxCache();
    }
}

public void clearTaxCache()
{
    // Intentional dummy method to call the method on the LedgerJournalTransDaily form.
}

public void close()
{
    ledgerJournalEngine_TAccount.close();
    ledgerJournalEngine_TAccount.formMethodClosePre();
    this.bookRefresh();

    super();
}

/// <summary>
/// Enables or disables credit fields.
/// </summary>
/// <param name="_allowEdit">
/// Indicates whether credit fields can be editable.
/// </param>
private void enableCreditFields(boolean _allowEdit)
{
    ledgerJournalTrans_Credit_ds.object(fieldNum(LedgerJournalTrans, AmountCurCredit)).allowEdit(_allowEdit);
    ledgerJournalTrans_Credit_ds.object(fieldNum(LedgerJournalTrans, Txt)).allowEdit(_allowEdit);
    ledgerJournalTrans_Credit_ds.object(fieldNum(LedgerJournalTrans, LedgerDimension)).allowEdit(_allowEdit);
    ledgerJournalTrans_Credit_ds.object(fieldNum(LedgerJournalTrans, CurrencyCode)).allowEdit(_allowEdit);
    ledgerJournalTrans_Credit_ds.object(fieldNum(LedgerJournalTrans, TaxGroup)).allowEdit(_allowEdit);
    ledgerJournalTrans_Credit_ds.object(fieldNum(LedgerJournalTrans, TaxItemGroup)).allowEdit(_allowEdit);
}

/// <summary>
/// To enable or disable data source for edit,create or delete.
/// </summary>
/// <param name="_formDataSource">
/// The data source to be enabled or disabled.
/// </param>
/// <param name="_allowEdit">
/// To determine whether to enable or disable data source
/// </param>
private void enableDataSource(FormDataSource _formDataSource, boolean _allowEdit)
{
    _formDataSource.allowEdit(_allowEdit);
    _formDataSource.allowCreate(_allowEdit);
    _formDataSource.allowDelete(_allowEdit);
}

/// <summary>
/// Enables or disables debit fields.
/// </summary>
/// <param name="_allowEdit">
/// Indicates whether debit fields can be editable.
/// </param>
private void enableDebitFields(boolean _allowEdit)
{
    LedgerJournalTrans_Debit_ds.object(fieldNum(LedgerJournalTrans, AmountCurDebit)).allowEdit(_allowEdit);
    LedgerJournalTrans_Debit_ds.object(fieldNum(LedgerJournalTrans, Txt)).allowEdit(_allowEdit);
    LedgerJournalTrans_Debit_ds.object(fieldNum(LedgerJournalTrans, LedgerDimension)).allowEdit(_allowEdit);
    LedgerJournalTrans_Debit_ds.object(fieldNum(LedgerJournalTrans, CurrencyCode)).allowEdit(_allowEdit);
    LedgerJournalTrans_Debit_ds.object(fieldNum(LedgerJournalTrans, TaxGroup)).allowEdit(_allowEdit);
    LedgerJournalTrans_Debit_ds.object(fieldNum(LedgerJournalTrans, TaxItemGroup)).allowEdit(_allowEdit);
}

/// <summary>
/// Enables or disables debit/credit fields according to whether debit/credit ledger dimension exists.
/// </summary>
/// <remarks>
/// It is called when a record is activated.
/// </remarks>
public void enableDRCRFields()
{
    if (ledgerJournalTrans_Debit.LedgerDimension != 0 || ledgerJournalTrans_Debit.RecId == 0)
    {
        this.enableDebitFields(true);
    }
    else
    {
        this.enableDebitFields(false);
    }

    if (ledgerJournalTrans_Credit.LedgerDimension != 0 || ledgerJournalTrans_Credit.RecId == 0)
    {
        this.enableCreditFields(true);
    }
    else
    {
        this.enableCreditFields(false);
    }
}

/// <summary>
/// LedgerDimension fields are editable only when voucher is not posted
/// and ledger journal type is <c>LedgerJournalACType::Ledger</c>.
/// </summary>
/// <remarks>
/// It is called when a record is activated.
/// </remarks>
public void enableLedgerDimensionFields()
{
    if (allowEdit)
    {
        if (ledgerJournalTrans_Debit.AccountType == LedgerJournalACType::Ledger)
        {
            ledgerJournalTrans_Debit_ds.object(fieldNum(ledgerJournalTrans, LedgerDimension)).enabled(true);
        }
        else
        {
            ledgerJournalTrans_Debit_ds.object(fieldNum(ledgerJournalTrans, LedgerDimension)).enabled(false);
        }

        if (ledgerJournalTrans_Credit.AccountType == LedgerJournalACType::Ledger)
        {
            ledgerJournalTrans_Credit_ds.object(fieldNum(ledgerJournalTrans, LedgerDimension)).enabled(true);
        }
        else
        {
            ledgerJournalTrans_Credit_ds.object(fieldNum(ledgerJournalTrans, LedgerDimension)).enabled(false);
        }

        if (ledgerJournalTrans_Debit.AccountType != LedgerJournalACType::Ledger
            || ledgerJournalTrans_Debit.OffsetAccountType != LedgerJournalACType::Ledger)
        {
            ledgerJournalTrans_Debit_ds.allowDelete(false);
        }
        else
        {
            ledgerJournalTrans_Debit_ds.allowDelete(true);
        }

        if (ledgerJournalTrans_Credit.AccountType != LedgerJournalACType::Ledger
            || ledgerJournalTrans_Credit.OffsetAccountType != LedgerJournalACType::Ledger)
        {
            ledgerJournalTrans_Credit_ds.allowDelete(false);
        }
        else
        {
            ledgerJournalTrans_Credit_ds.allowDelete(true);
        }
    }
}

public void init()
{
    #isoCountryRegionCodes
    FormRun callerForm;

    if (!LedgerParameters::isTAccount())
    {
        // %1 parameter must be selected in %2 parameters to activate the %3 feature.
        // General ledger
        // T-account (JP)
        throw warning (strFmt("@GLS60241",fieldPName(LedgerParameters, tAccount_JP),"@SYS24097","@GLS60236"));
    }

    if (!element.args().caller())
    {
        // Form %1 requires an active buffer.
        throw error(strFmt("@SYS22678",element.name()));
    }

    super();

    GlobalizationInstrumentationHelper::featureRun(FeatureReference, funcName());

    ledgerJournalEngine_TAccount         = LedgerJournalEngine_TAccount::construct(element);
    callerForm                                  = element.args().caller();

    ledgerJournalTable                          = callerForm.args().record();
    ledgerJournalEngine_TAccount.ledgerJournalTable(ledgerJournalTable);
    ledgerJournalId                             = ledgerJournalTable.JournalNum;

    ledgerJournalEngine_TAccount.parmLedgerJournalTable(ledgerJournalTable);
    ledgerJournalEngine_TAccount.enableVoucher();

    allowEdit = LedgerJournalEngine_TAccount.allowEdit();

    this.enableDataSource(ledgerJournalTrans_Debit_ds, allowEdit);
    this.enableDataSource(ledgerJournalTrans_Credit_ds, allowEdit);

    // For debit SEC controls
    LedgerJournalTrans_Debit_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);
    AccountSelectionForDebit_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_Debit_LedgerDimension.parmValidateBlockedForManualEntry(true);
    AccountSelectionForDebit_LedgerDimension.parmValidateBlockedForManualEntry(true);
    LedgerJournalTrans_Debit_LedgerDimension.parmJournalName(ledgerJournalTable.JournalName);
    AccountSelectionForDebit_LedgerDimension.parmJournalName(ledgerJournalTable.JournalName);
    LedgerJournalTrans_Debit_LedgerDimension.parmCurrency(ledgerJournalTrans_Debit.CurrencyCode);
    AccountSelectionForDebit_LedgerDimension.parmCurrency(ledgerJournalTrans_Debit.CurrencyCode);
    LedgerJournalTrans_Debit_LedgerDimension.parmDataAreaId(ledgerJournalTrans_Debit.Company ? ledgerJournalTrans_Debit.Company : curext());
    AccountSelectionForDebit_LedgerDimension.parmDataAreaId(ledgerJournalTrans_Debit.Company ? ledgerJournalTrans_Debit.Company : curext());
    LedgerJournalTrans_Debit_LedgerDimension.parmControlDate(ledgerJournalTrans_Debit.TransDate);
    AccountSelectionForDebit_LedgerDimension.parmControlDate(ledgerJournalTrans_Debit.TransDate);

    // For credit SEC controls
    LedgerJournalTrans_Credit_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);
    AccountSelectionForCredit_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);
    LedgerJournalTrans_Credit_LedgerDimension.parmValidateBlockedForManualEntry(true);
    AccountSelectionForCredit_LedgerDimension.parmValidateBlockedForManualEntry(true);
    LedgerJournalTrans_Credit_LedgerDimension.parmJournalName(ledgerJournalTable.JournalName);
    AccountSelectionForCredit_LedgerDimension.parmJournalName(ledgerJournalTable.JournalName);
    LedgerJournalTrans_Credit_LedgerDimension.parmCurrency(ledgerJournalTrans_Credit.CurrencyCode);
    AccountSelectionForCredit_LedgerDimension.parmCurrency(ledgerJournalTrans_Credit.CurrencyCode);
    LedgerJournalTrans_Credit_LedgerDimension.parmDataAreaId(ledgerJournalTrans_Credit.Company ? ledgerJournalTrans_Credit.Company : curext());
    AccountSelectionForCredit_LedgerDimension.parmDataAreaId(ledgerJournalTrans_Credit.Company ? ledgerJournalTrans_Credit.Company : curext());
    LedgerJournalTrans_Credit_LedgerDimension.parmControlDate(ledgerJournalTrans_Credit.TransDate);
    AccountSelectionForCredit_LedgerDimension.parmControlDate(ledgerJournalTrans_Credit.TransDate);
}

