/// <summary>
/// Parms current TransDate and Voucher to <c>LedgerJournalEngine_TAccount</c> instance.
/// </summary>
private void setLedgerJournalEngine_TAccount()
{
    ledgerJournalEngine_TAccount.parmTransDate(ledgerJournalTrans_Debit.TransDate);
    ledgerJournalEngine_TAccount.parmVoucher(ledgerJournalTrans_Debit.Voucher);
}

public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    queryBuildDataSource = this.query().dataSourceTable(tableNum(LedgerJournalTrans));
    queryBuildDataSource.addSortField(fieldNum(LedgerJournalTrans, Voucher));

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(LedgerJournalTrans, JournalNum));
    queryBuildRange.value(ledgerJournalTable.JournalNum);
    queryBuildRange.status(RangeStatus::Hidden);

    queryBuildRange = queryBuildDataSource.addRange(fieldNum(LedgerJournalTrans, AmountCurDebit));
    queryBuildRange.value(SysQuery::valueNot(0));
    queryBuildRange.status(RangeStatus::Hidden);

    super();
}

public void create(boolean _append = false)
{
    if (ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit())
    {
        ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit().formMethodDataSourceCreatePre();
    }

    super(_append);
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.enableLedgerDimensionFields();
        element.enableDRCRFields();
        this.setLedgerJournalEngine_TAccount();
    }
    return ret;
}

public void linkActive()
{
    ledgerJournalEngine_TAccount.formMethodDataSourceLinkActivePre();

    super();
}

public void delete()
{
    ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit().formMethodDataSourceDelete();

    super();

    ledgerJournalTrans_Debit_ds.refresh();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        ret = ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit().formMethodDataSourceValidateWrite(ret);
    }

    if (ledgerJournalTrans_Debit.LedgerDimension == 0)
    {
        // Account number must be specified.
        ret = checkFailed("@SYS25989");
    }

    return ret;
}

public void write()
{
    NumberSeqFormHandlerLedgerJournal   numberSeqFormHandlerJournal;

    numberSeqFormHandlerJournal = ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit();
    numberSeqFormHandlerJournal.formMethodDataSourceWrite();

    super();
}

void initValue()
{
    if (ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit())
    {
        ledgerJournalEngine_TAccount.numberSeqFormHandlerJournalDebit().formMethodDataSourceCreatePre();
    }

    // Removes super() because it is called in method initValue of class LedgerJournalEngine_TAccount
    ledgerJournalEngine_TAccount.initValue(ledgerJournalTrans_Debit, null, true);
}

display TaxAmountJournal dispSalesTaxAmountDebit()
{
    TaxAmountJournal    taxAmountJournal;

    if (ledgerJournalTrans_Debit.isTaxable())
    {
        taxAmountJournal = abs(ledgerJournalTrans_Debit.totalTaxAmountSingleLine(false, true, true));
    }

    return taxAmountJournal;
}

public void modified()
{
    if (ledgerJournalTrans_Debit.AccountType == LedgerJournalACType::Project ||
       (ledgerJournalTrans_Debit.OffsetAccountType == LedgerJournalACType::Project && ledgerJournalTrans_Debit.parmOffsetLedgerDimension()))
    {
        // The logic for project need special handling and it was moved to the LedgerJournalEngine_TAccount class
        LedgerJournalEngine_TAccount::amountCurCreditDebitModifiedOnServer(
            ledgerJournalTrans_Debit.RecId,
            ledgerJournalTrans_Debit.AmountCurDebit,
            true);
        ledgerJournalTrans_Debit_ds.reread();
    }
    else
    {
        super();
    }
    ledgerJournalTrans_Debit_ds.refresh();
}

public void modified()
{
    super();

    LedgerJournalTrans_Debit_LedgerDimension.parmDataAreaId(ledgerJournalTrans_Debit.Company);
    AccountSelectionForDebit_LedgerDimension.parmDataAreaId(ledgerJournalTrans_Debit.Company);
}

