public boolean validateWrite()
{
    boolean isValid;
    ReasonRefRecID reasonRef = ledgerJournalTrans_ds.object(fieldNum(TmpLedgerConsElimination, ReasonRefRecId)).getValue();

    isValid = super();

    if (isValid && !ledgerJournalEngine.formMethodDataSourceValidateWritePost())
    {
        isValid = false;
    }

    if (isValid && reasonRef)
    {
        //Check that the reason code is valid for the Ledger account type
        if (!ReasonFormTable::checkReasonCodeForAccountType(reasonRef,LedgerJournalACType::Ledger))
        {
            // The Reason code is not active for account type
            isValid = checkFailed("@SYS115522");
        }
    }

    return isValid;
}

